// WARNING: This script is designed to work with a remote JS executor. Compatibility with a local JS evaluator is not guaranteed and may require rewriting.

const message = decodeToJson(payload);
const values = Object.assign(
  decodeDevicePayload(message.DevEUI_uplink.payload_hex),
  extractNetworkTelemetry(message)
);

return {
  deviceName: String(values.device_id),
  deviceType: 'DL-ATM41',
  attributes: {
    lora_dev_eui: values.lora_dev_eui,
    protocol_version: values.protocol_version,
  },
  telemetry: [{
    ts: parseDateToTimestamp(message.DevEUI_uplink.Time),
    values: keepTelemetry(values)
  }]
};

function decodeDevicePayload(payload) {
  /* https://www.decentlab.com/products/eleven-parameter-weather-station-for-lorawan */
  const decentlab_decoder = {
    PROTOCOL_VERSION: 2,
    SENSORS: [
      {length: 17,
       values: [{name: 'solar_radiation',
                 displayName: 'Solar radiation',
                 convert: function (x) { return x[0] - 32768; },
                 unit: 'W⋅m⁻²'},
                {name: 'precipitation',
                 displayName: 'Precipitation',
                 convert: function (x) { return (x[1] - 32768) / 1000; },
                 unit: 'mm'},
                {name: 'lightning_strike_count',
                 displayName: 'Lightning strike count',
                 convert: function (x) { return x[2] - 32768; }},
                {name: 'lightning_average_distance',
                 displayName: 'Lightning average distance',
                 convert: function (x) { return x[3] - 32768; },
                 unit: 'km'},
                {name: 'wind_speed',
                 displayName: 'Wind speed',
                 convert: function (x) { return (x[4] - 32768) / 100; },
                 unit: 'm⋅s⁻¹'},
                {name: 'wind_direction',
                 displayName: 'Wind direction',
                 convert: function (x) { return (x[5] - 32768) / 10; },
                 unit: '°'},
                {name: 'maximum_wind_speed',
                 displayName: 'Maximum wind speed',
                 convert: function (x) { return (x[6] - 32768) / 100; },
                 unit: 'm⋅s⁻¹'},
                {name: 'air_temperature',
                 displayName: 'Air temperature',
                 convert: function (x) { return (x[7] - 32768) / 10; },
                 unit: '°C'},
                {name: 'vapor_pressure',
                 displayName: 'Vapor pressure',
                 convert: function (x) { return (x[8] - 32768) / 100; },
                 unit: 'kPa'},
                {name: 'atmospheric_pressure',
                 displayName: 'Atmospheric pressure',
                 convert: function (x) { return (x[9] - 32768) / 100; },
                 unit: 'kPa'},
                {name: 'relative_humidity',
                 displayName: 'Relative humidity',
                 convert: function (x) { return (x[10] - 32768) / 10; },
                 unit: '%'},
                {name: 'sensor_temperature_internal',
                 displayName: 'Sensor temperature (internal)',
                 convert: function (x) { return (x[11] - 32768) / 10; },
                 unit: '°C'},
                {name: 'x_orientation_angle',
                 displayName: 'X orientation angle',
                 convert: function (x) { return (x[12] - 32768) / 10; },
                 unit: '°'},
                {name: 'y_orientation_angle',
                 displayName: 'Y orientation angle',
                 convert: function (x) { return (x[13] - 32768) / 10; },
                 unit: '°'},
                {name: 'compass_heading',
                 displayName: 'Compass heading',
                 convert: function (x) { return x[14] - 32768; },
                 unit: '°'},
                {name: 'north_wind_speed',
                 displayName: 'North wind speed',
                 convert: function (x) { return (x[15] - 32768) / 100; },
                 unit: 'm⋅s⁻¹'},
                {name: 'east_wind_speed',
                 displayName: 'East wind speed',
                 convert: function (x) { return (x[16] - 32768) / 100; },
                 unit: 'm⋅s⁻¹'}]},
      {length: 1,
       values: [{name: 'battery_voltage',
                 displayName: 'Battery voltage',
                 convert: function (x) { return x[0] / 1000; },
                 unit: 'V'}]}
    ],
    read_int: function (bytes, pos) {
      return (bytes[pos] << 8) + bytes[pos + 1];
    },
    decode: function (msg) {
      var bytes = msg;
      var i, j;
      if (typeof msg === 'string') {
        bytes = [];
        for (i = 0; i < msg.length; i += 2) {
          bytes.push(parseInt(msg.substring(i, i + 2), 16));
        }
      }
      var version = bytes[0];
      if (version != this.PROTOCOL_VERSION) {
        return {error: "protocol version " + version + " doesn't match v2"};
      }
      var deviceId = this.read_int(bytes, 1);
      var flags = this.read_int(bytes, 3);
      var result = {'protocol_version': version, 'device_id': deviceId};
      // decode payload
      var pos = 5;
      for (i = 0; i < this.SENSORS.length; i++, flags >>= 1) {
        if ((flags & 1) !== 1)
          continue;
        var sensor = this.SENSORS[i];
        var x = [];
        // convert data to 16-bit integer array
        for (j = 0; j < sensor.length; j++) {
          x.push(this.read_int(bytes, pos));
          pos += 2;
        }
        // decode sensor values
        for (j = 0; j < sensor.values.length; j++) {
          var value = sensor.values[j];
          if ('convert' in value) {
            result[value.name] = {displayName: value.displayName,
                                  value: value.convert.bind(this)(x)};
            if ('unit' in value)
              result[value.name]['unit'] = value.unit;
          }
        }
      }
      return result;
    }
  };

  const decoded = decentlab_decoder.decode(payload);
  const result = {};
  for (var k in decoded) {
    if (typeof decoded[k] === "object") {
      result[k] = decoded[k].value;
    } else if (typeof decoded[k] === "number") {
      result[k] = decoded[k];
    }
  }
  return result;
}

function extractNetworkTelemetry(payload) {
  return {
      lora_frame_counter: message.DevEUI_uplink.FCntUp,
      lora_frame_port: message.DevEUI_uplink.FPort,
      lora_frequency: message.DevEUI_uplink?.Frequency * 1000000,
      lora_spreading_factor: message.DevEUI_uplink.SpFact,
      lora_dev_eui: message.DevEUI_uplink.DevEUI,
      lora_rssi: message.DevEUI_uplink.LrrRSSI,
      lora_snr: message.DevEUI_uplink.LrrSNR,
  };
}

// Helper functions

function decodeToJson(payload) {
  const s = String.fromCharCode.apply(String, payload);
  return JSON.parse(s);
}

function parseDateToTimestamp(dateString) {
  var timestamp = -1;
  if (dateString != null) {
    timestamp = new Date(dateString).getTime();
    if (timestamp == -1) {
      var secondsSeparatorIndex = dateString.lastIndexOf('.') + 1;
      var millisecondsEndIndex = dateString.lastIndexOf('+');
      if (millisecondsEndIndex == -1) {
        millisecondsEndIndex = dateString.lastIndexOf('Z');
      }
      if (millisecondsEndIndex == -1) {
        millisecondsEndIndex = dateString.lastIndexOf('-');
      }
      if (millisecondsEndIndex == -1) {
        if (dateString.length >= secondsSeparatorIndex + 3) {
          dateString = dateString.substring(0, secondsSeparatorIndex + 3);
        }
      } else {
        dateString = dateString.substring(0, secondsSeparatorIndex + 3) +
          dateString.substring(millisecondsEndIndex, dateString.length);
      }
      timestamp = new Date(dateString).getTime();
    }
  }
  // If we cannot parse timestamp - we will use the current timestamp
  if (timestamp == -1) {
    timestamp = Date.now();
  }

  return timestamp;
}

function base64ToBytes(base64String) {
  if (typeof base64String === 'string') {
    return Uint8Array.from(atob(base64String), c => c.charCodeAt(0));
  }
  return [];
}

function keepTelemetry(telemetry) {
  const unwanted = ['protocol_version', 'device_id', 'lora_dev_eui'];
  return Object.fromEntries(
    Object.entries(telemetry).filter(
      (x) => !unwanted.includes(x[0])
    )
  );
}
