function decodePayload(input) {
    var result = { attributes: {}, telemetry: {} };
    var timestamp = metadata.ts;
    var values = {};

    if (parseBytesToInt(input, 0, 1) === 1) {
        values = handleKeepalive(input, values);
    } else {
        values = handleResponse(input, values);
        var tail = input.slice(-12);
        values = handleKeepalive(tail, values);
    }

    result.telemetry = {
        ts: timestamp,
        values: values
    };
    return result;
}

function handleKeepalive(bytes, data) {
    data.internalTemperature = parseBytesToInt(bytes, 1, 1);
    data.relayState = (parseBytesToInt(bytes, 11, 1) === 0x01 ? "ON" : "OFF");

    var energy = parseBytesToInt(bytes, 2, 4);
    var power  = parseBytesToInt(bytes, 6, 2);
    var acCurr = parseBytesToInt(bytes, 9, 2);
    var acVolt = parseBytesToInt(bytes, 8, 1);

    data.energy_kWh = energy / 1000;
    data.power_W = power;
    data.acVoltage_V = acVolt;
    data.acCurrent_mA = acCurr;

    return data;
}

function handleResponse(bytes, data) {
    var commands = [];

    var len = bytes.length - 12;

    for (var i = 0; i < len; i++) {
        var b = bytes[i] & 0xFF;
        var h = intToHex(b);

        if (h.length == 1) {
            h = '0' + h;
        } 
        commands.add(h);
    }

    for (var i = 0; i < commands.length; i++) {
        var cmd = commands[i];
        switch (cmd) {
            case '04':
                data.deviceVersions = {
                    hardware: parseInt(commands[i+1], 16),
                    software: parseInt(commands[i+2], 16)
                };
                i += 2;
                break;
            case '12':
                data.keepAliveTime = parseInt(commands[i+1], 16);
                i += 1;
                break;
            case '19':
                data.joinRetryPeriod = parseInt(commands[i+1], 16) * 5 / 60;
                i += 1;
                break;
            case '1b':
                data.uplinkType = parseInt(commands[i+1], 16);
                i += 1;
                break;
            case '1d':
                var wdpC  = (commands[i+1] != '00') ? parseInt(commands[i+1], 16) : false;
                var wdpUc = (commands[i+2] != '00') ? parseInt(commands[i+2], 16) : false;
                data.watchDogParams = { wdpC: wdpC, wdpUc: wdpUc };
                i += 2;
                break;
            case '1f':
                data.overheatingThreshold = parseInt(commands[i+1], 16);
                i += 1;
                break;
            case '21':
                data.overvoltageThreshold = parseInt(commands[i+1] + commands[i+2], 16);
                i += 2;
                break;
            case '23':
                data.overcurrentThreshold = parseInt(commands[i+1], 16);
                i += 1;
                break;
            case '25':
                data.overpowerThreshold = parseInt(commands[i+1] + commands[i+2], 16);
                i += 2;
                break;
            case '5a':
                data.afterOverheatingProtectionRecovery = parseInt(commands[i+1], 16);
                i += 1;
                break;
            case '5c':
                data.ledIndicationMode = parseInt(commands[i+1], 16);
                i += 1;
                break;
            case '5d':
                data.manualChangeRelayState = (parseInt(commands[i+1], 16) === 0x01);
                i += 1;
                break;
            case '5f':
                data.relayRecoveryState = parseInt(commands[i+1], 16);
                i += 1;
                break;
            case '60':
                data.overheatingEvents = {
                    events:      parseInt(commands[i+1], 16),
                    temperature: parseInt(commands[i+2], 16)
                };
                i += 2;
                break;
            case '61':
                data.overvoltageEvents = {
                    events:  parseInt(commands[i+1], 16),
                    voltage: parseInt(commands[i+2] + commands[i+3], 16)
                };
                i += 3;
                break;
            case '62':
                data.overcurrentEvents = {
                    events:  parseInt(commands[i+1], 16),
                    current: parseInt(commands[i+2] + commands[i+3], 16)
                };
                i += 3;
                break;
            case '63':
                data.overpowerEvents = {
                    events: parseInt(commands[i+1], 16),
                    power:  parseInt(commands[i+2] + commands[i+3], 16)
                };
                i += 3;
                break;
            case '70':
                data.overheatingRecoveryTime = parseInt(commands[i+1] + commands[i+2], 16);
                i += 2;
                break;
            case '71':
                data.overvoltageRecoveryTime = parseInt(commands[i+1] + commands[i+2], 16);
                i += 2;
                break;
            case '72':
                data.overcurrentRecoveryTemp = parseInt(commands[i+1], 16);
                i += 1;
                break;
            case '73':
                data.overpowerRecoveryTemp = parseInt(commands[i+1], 16);
                i += 1;
                break;
            case 'b1':
                data.relayState = (parseInt(commands[i+1], 16) === 0x01);
                i += 1;
                break;
            case 'a0':
                var addrRaw = commands[i+1] + commands[i+2] + commands[i+3] + commands[i+4];
                data.fuota = {
                    fuota_address: parseInt(addrRaw, 16),
                    fuota_address_raw: addrRaw
                };
                i += 4;
                break;
            default:
                break;
        }
    }
    return data;
}

var result = {};

if (payload != null && payload.length > 0) {
    result = decodePayload(payload);
}

return result;
