//    "telemetry": {"telemetryKey": "telemetryValue"}
//  }

function decodePayload(input) {
    var output = { attributes: {}, telemetry: []};

    // --- Decoding code --- //
    var decoded = {};
    var historyDataList = [];
    for (var i = 0; i < input.length - 2; ) {
        var channel_id = input[i++] & 0xff;
        var channel_type = input[i++] & 0xff;
        // BATTERY
        if(channel_id === 0x01 && channel_type === 0x75) {
            decoded.battery = input[i]; 
            i += 1;
        }
        // TEMPERATURE
        if (channel_id === 0x03 && channel_type === 0x67) {
            // ℃
            decoded.temperature = parseBytesToInt(input, i, 2, false) / 10;
            // ℉
            // decoded.temperature = parseBytesToInt(input, i, 2, false) / 10 * 1.8 + 32;
            i += 2;
        }
        // HUMIDITY
        if (channel_id === 0x04 && channel_type === 0x68) {
            decoded.humidity = parseBytesToInt(input, i, 1, false) / 2;
            i += 1;
        }
        // PIR
        if (channel_id === 0x05 && channel_type === 0x00) {
            decoded.pir = input[i] === 1 ? "trigger" : "idle";
            i += 1;
        }
        // LIGHT
        if (channel_id === 0x06 && channel_type === 0xcb) {
            decoded.light_level = input[i];
            i += 1;
        }
        // CO2
        if (channel_id === 0x07 && channel_type === 0x7d) {
            decoded.co2 = parseBytesToInt(input, i, 2, false);
            i += 2;
        }
        // TVOC
        if (channel_id === 0x08 && channel_type === 0x7d) {
            decoded.tvoc = parseBytesToInt(input, i, 2, false);
            i += 2;
        }
        // PRESSURE
        if (channel_id === 0x09 && channel_type === 0x73) {
            decoded.pressure = parseBytesToInt(input, i, 2, false) / 10;
            i += 2;
        }
        // BEEP
        if (channel_id === 0x0e && channel_type === 0x01) {
            decoded.beep = input[i] === 1 ? "yes" : "no";
            i += 1;
        }
        // HISTORY DATA
        if (channel_id === 0x20 && channel_type === 0xce) {
           var historyData = {
                ts: parseBytesToInt(input, i, 4, false) * 1000,
                values: {
                    temperature: parseBytesToInt(input, i + 4, 2, false) / 10,
                    humidity: parseBytesToInt(input, i + 6, 2, false) / 2,
                    pir: input[i + 8] === 1 ? "trigger" : "idle",
                    light_level: input[i + 9],
                    co2: parseBytesToInt(input, i + 10, 2, false),
                    tvoc: parseBytesToInt(input, i + 12, 2, false),
                    pressure: parseBytesToInt(input, i + 14, 2, false) / 10
                }
            };

            historyDataList.add(historyData);

            i += 16;
        }
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    output.telemetry.addAll(historyDataList);

    // --- Decoding code --- //
    return output;
}

