function decodePayload(input) {
    var output = {
        attributes: {},
        telemetry: []
    };

    // --- Decoding code --- //
    var decoded = {};
    var historyData = {};
    for (var i = 0; i < input.length - 2;) {
        var channel_id = input[i++] & 0xff;
        var channel_type = input[i++] & 0xff;
        // BATTERY
        if (channel_id === 0x01 && channel_type === 0x75) {
            decoded.battery = input[i];
            i += 1;
        }
        // TEMPERATURE
        if (channel_id === 0x03 && channel_type === 0x67) {
            // ℃
            decoded.temperature = parseBytesToInt(input, i,
                2, false) / 10;
            i += 2;
            // ℉
            // decoded.temperature = parseBytesToInt(input, i, 2, false) / 10 * 1.8 + 32;
            // i +=2;
        }
        // HUMIDITY
        if (channel_id === 0x04 && channel_type === 0x68) {
            decoded.humidity = input[i] / 2;
            i += 1;
        }
        // PIR
        if (channel_id === 0x05 && channel_type === 0x00) {
            decoded.pir = input[i] === 1 ? "trigger" :
                "idle";
            i += 1;
        }
        // LIGHT
        if (channel_id === 0x06 && channel_type === 0xCB) {
            decoded.light_level = input[i];
            i += 1;
        }
        // CO2
        if (channel_id === 0x07 && channel_type === 0x7D) {
            decoded.co2 = parseBytesToInt(input, i, 2,
                false);
            i += 2;
        }
        // TVOC
        if (channel_id === 0x08 && channel_type === 0x7D) {
            decoded.tvoc = parseBytesToInt(input, i, 2,
                false);
            i += 2;
        }
        // PRESSURE
        if (channel_id === 0x09 && channel_type === 0x73) {
            decoded.pressure = parseBytesToInt(input, i, 2,
                false) / 10;
            i += 2;
        }
        // HCHO
        if (channel_id === 0x0A && channel_type === 0x7D) {
            decoded.hcho = parseBytesToInt(input, i, 2,
                false) / 100;
            i += 2;
        }
        // PM2.5
        if (channel_id === 0x0B && channel_type === 0x7D) {
            decoded.pm2_5 = parseBytesToInt(input, i, 2,
                false);
            i += 2;
        }
        // PM10
        if (channel_id === 0x0C && channel_type === 0x7D) {
            decoded.pm10 = parseBytesToInt(input, i, 2,
                false);
            i += 2;
        }
        // O3
        if (channel_id === 0x0D && channel_type === 0x7D) {
            decoded.o3 = parseBytesToInt(input, i, 2,
                false) / 100;
            i += 2;
        }
        // BEEP
        if (channel_id === 0x0E && channel_type === 0x01) {
            decoded.beep = input[i] === 1 ? "yes" : "no";
            i += 1;
        }
        // HISTORY DATA (AM308)
        if (channel_id === 0x20 && channel_type === 0xCE) {
            var historyData = {
                ts: parseBytesToInt(input, i, 4, false),
                values: {
                    temperature: parseBytesToInt(input, i + 4, 2, false) / 10,
                    humidity: parseBytesToInt(input, i + 6, 2, false) / 2,
                    pir: input[i + 8] === 1 ? "trigger" : "idle",
                    light_level: input[i + 9],
                    co2: parseBytesToInt(input, i + 10, 2, false),
                    tvoc: parseBytesToInt(input, i + 12, 2, false),
                    pressure: parseBytesToInt(input, i + 14, 2, false) / 10,
                    pm2_5: parseBytesToInt(input, i + 16, 2, false),
                    pm10: parseBytesToInt(input, i + 18, 2, false)
                }
            };

            i += 20;
        }
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }, historyData];

    // --- Decoding code --- //
    return output;
}

