function decodePayload(input) {
    var output = {
        attributes: {},
        telemetry: []
    };

    // --- Decoding code --- //
    var decoded = {};
    for (var i = 0; i < input.length - 2;) {
        var channel_id = input[i++] & 0xff;
        var channel_type = input[i++] & 0xff;

        // POWER STATE
        if (channel_id === 0xff && channel_type === 0x0b) {
            decoded.power = "on";
            i += 1;
        }
        // IPSO VERSION
        if (channel_id === 0xff && channel_type === 0x01) {
            decoded.ipso_version = readProtocolVersion(input[i]);
            i += 1;
        }
        // PRODUCT SERIAL NUMBER
        if (channel_id === 0xff && channel_type === 0x16) {
            decoded.sn = bytesToHex(java.util.Arrays.copyOfRange(input, i, i + 8));
            i += 8;
        }
        // HARDWARE VERSION
        if (channel_id === 0xff && channel_type === 0x09) {
            decoded.hardware_version = readHardwareVersion(java.util.Arrays.copyOfRange(input, i, i + 2));
            i += 2;
        }
        // FIRMWARE VERSION
        if (channel_id === 0xff && channel_type === 0x0a) {
            decoded.firmware_version = readFirmwareVersion(java.util.Arrays.copyOfRange(input, i, i + 2));
            i += 2;
        }
        // TOTAL CURRENT
        if (channel_id === 0x03 && channel_type === 0x97) {
            decoded.total_current = parseBytesToInt(input, i, 4, false) / 100;
            i += 4;
        }
        // CURRENT
        if (channel_id === 0x04 && channel_type === 0x98) {
            var value = parseBytesToInt(input, i, 2, false);
            if (value === 0xffff) {
                decoded.alarm = "read failed";
            } else {
                decoded.current = value / 100;
            }
            i += 2;
        }
         // TEMPERATURE
        if (channel_id === 0x09 && channel_type === 0x67) {
            var temperature_value = parseBytesToInt(input, i, 2, false);
            if (temperature_value === 0xfffd) {
                decoded.temperature_exception = "over range alarm";
            } else if (temperature_value === 0xffff) {
                decoded.temperature_exception = "read failed";
            } else {
                decoded.temperature = parseBytesToInt(input, i, 2, false) / 10;
            }
            i += 2;
        }
        // CURRENT ALARM
        if (channel_id === 0x84 && channel_type === 0x98) {
            decoded.current_max = parseBytesToInt(input, i, 2, false) / 100;
            decoded.current_min = parseBytesToInt(input, i + 2, 2, false) / 100;
            decoded.current = parseBytesToInt(input, i + 4, 2, false) / 100;
            decoded.alarm = readCurrentAlarm(input[i + 6]);
            i += 7;
        }
        // TEMPERATURE ALARM
        if (channel_id === 0x89 && channel_type === 0x67) {
            decoded.temperature = parseBytesToInt(input, i, 2, false) / 10;
            decoded.temperature_alarm = readTemperatureAlarm(input[i + 2]);
            i += 3;
        }
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    // --- Decoding code --- //
    return output;
}

