// Decode an uplink message from a buffer
// payload - array of bytes
// metadata - key/value object

/** Decoder **/

// decode payload to string
var payloadStr = decodeToString(payload);

// decode payload to JSON
// var data = decodeToJson(payload);

var deviceName = 'Device A';
var deviceType = 'thermostat';
var customerName = 'Customer C';
var groupName = 'thermostat devices';
var manufacturer = 'Example corporation';
// use assetName and assetType instead of deviceName and deviceType
// to automatically create assets instead of devices.
// var assetName = 'Asset A';
// var assetType = 'building';

// Result object with device/asset attributes/telemetry data
var result = {
// Use deviceName and deviceType or assetName and assetType, but not both.
   deviceName: deviceName,
   deviceType: deviceType,
// assetName: assetName,
// assetType: assetType,
// customerName: customerName,
   groupName: groupName,
   attributes: {
       model: 'Model A',
       serialNumber: 'SN111',
       integrationName: metadata['integrationName'],
       manufacturer: manufacturer
   },
   telemetry: {
       temperature: 42,
       humidity: 80,
       rawData: payloadStr
   }
};

/** Helper functions **/

function decodeToString(payload) {
   return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
   // covert payload to string.
   var str = decodeToString(payload);

   // parse string to JSON
   var data = JSON.parse(str);
   return data;
}

return result;

configuration.tbelDecoder:
var data = decodeToJson(payload);
var deviceName = "CT101/CT103 " + data.EUI;
var deviceType = "CT101/CT103";
var groupName = null; // If groupName is not null - created device will be added to the entity group with such name.
var customerName = null; // If customerName is not null - created devices will be assigned to customer with such name. 

// use assetName and assetType instead of deviceName and deviceType
// to automatically create assets instead of devices.
// var assetName = 'Asset A';
// var assetType = 'building';

// If you want to parse incoming data somehow, you can add your code to this function.
// input: bytes
// expected output:
//  {
//    "attributes": {"attributeKey": "attributeValue"},
//    "telemetry": {"telemetryKey": "telemetryValue"}
//  }

function decodePayload(input) {
    var output = { attributes: {}, telemetry: []};

    // --- Decoding code --- //
    var decoded = {};
    for (var i = 0; i < input.length - 2; ) {
        var channel_id = input[i++] & 0xff;
        var channel_type = input[i++] & 0xff;

        // POWER STATE
        if (channel_id === 0xff && channel_type === 0x0b) {
            decoded.power = "on";
            i += 1;
        }
        // IPSO VERSION
        if (channel_id === 0xff && channel_type === 0x01) {
            decoded.ipso_version = readProtocolVersion(input[i]);
            i += 1;
        }
        // PRODUCT SERIAL NUMBER
        if (channel_id === 0xff && channel_type === 0x16) {
            decoded.sn = bytesToHex(input.slice(i, i + 8));
            i += 8;
        }
        // HARDWARE VERSION
        if (channel_id === 0xff && channel_type === 0x09) {
            decoded.hardware_version = readHardwareVersion(input.slice(i, i + 2));
            i += 2;
        }
        // FIRMWARE VERSION
        if (channel_id === 0xff && channel_type === 0x0a) {
            decoded.firmware_version = readFirmwareVersion(input.slice(i, i + 2));
            i += 2;
        }
        // TOTAL CURRENT
        if (channel_id === 0x03 && channel_type === 0x97) {
            decoded.total_current = parseBytesToInt(input, i, 4, false) / 100;
            i += 4;
        }
        // CURRENT
        if (channel_id === 0x04 && channel_type === 0x98) {
            var value = parseBytesToInt(input, i, 2, false);
            if (value === 0xffff) {
                decoded.alarm = "read failed";
            } else {
                decoded.current = value / 100;
            }
            i += 2;
        }
         // TEMPERATURE
        if (channel_id === 0x09 && channel_type === 0x67) {
            var temperature_value = parseBytesToInt(input, i, 2, false);
            if (temperature_value === 0xfffd) {
                decoded.temperature_exception = "over range alarm";
            } else if (temperature_value === 0xffff) {
                decoded.temperature_exception = "read failed";
            } else {
                decoded.temperature = parseBytesToInt(input, i, 2, false) / 10;
            }
            i += 2;
        }
        // CURRENT ALARM
        if (channel_id === 0x84 && channel_type === 0x98) {
            decoded.current_max = parseBytesToInt(input, i, 2, false) / 100;
            decoded.current_min = parseBytesToInt(input, i+2, 2, false) / 100;
            decoded.current = parseBytesToInt(input, i+4, 2, false) / 100;
            decoded.alarm = readCurrentAlarm(input[i + 6]);
            i += 7;
        }
        // TEMPERATURE ALARM
        if (channel_id === 0x89 && channel_type === 0x67) {
            decoded.temperature = parseBytesToInt(input, i, 2, false) / 10;
            decoded.temperature_alarm = readTemperatureAlarm(input[i + 2]);
            i += 3;
        }
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    // --- Decoding code --- //
    return output;
}

// --- attributes and telemetry objects ---
var telemetry = [];
var attributes = {};
// --- attributes and telemetry objects ---

// --- Timestamp parsing
timestamp = data.ts;
// --- Timestamp parsing

// Message parsing
// To avoid paths in the decoded objects we passing false value to function as "pathInKey" argument.
// Warning: pathInKey can cause already found fields to be overwritten with the last value found.

var uplinkDataList = [];

// Passing incoming bytes to decodePayload function, to get custom decoding
var customDecoding = decodePayload(hexToBytes(data.data));

// Collecting data to result
if (customDecoding.?telemetry.size() > 0) {
    if (customDecoding.telemetry instanceof java.util.ArrayList) {
        foreach(telemetryObj: customDecoding.telemetry) {
            if (telemetryObj.ts != null && telemetryObj.values != null) {
                telemetry.add(telemetryObj);
            }
        }
    } else {
        telemetry.putAll(customDecoding.telemetry);
    }
}

if (customDecoding.?attributes.size() > 0) {
    attributes.putAll(customDecoding.attributes);
}

// You can add some keys manually to attributes or telemetry
attributes.eui = data.EUI;
attributes.fPort = data.port;
attributes.frequency = data.freq;

var addDataToTelemetry = {};
addDataToTelemetry.rssi = data.rssi;
addDataToTelemetry.seqno = data.seqno;
addDataToTelemetry.snr = data.snr;
addDataToTelemetry.ack = data.ack;
addDataToTelemetry.toa = data.toa;
addDataToTelemetry.fCnt = data.fcnt;

var isIncludeGatewayInfo = metadata["includeGatewayInfo"];
if(isIncludeGatewayInfo == true) {
   telemetry = processTelemetryData(telemetry, addDataToTelemetry);
}

var deviceInfo = {
    deviceName: deviceName,
    deviceType: deviceType,
//  assetName: assetName,
//  assetType: assetType,
    attributes: attributes,
    telemetry: telemetry, 
};

addAdditionalInfoForDeviceMsg(deviceInfo, customerName, groupName);

uplinkDataList.add(deviceInfo);

var includeGatewayInfo = ["ts", "gweui", "rssi"];

var gatewayDeviceNamePrefix = "Gateway ";
var gatewayDeviceType = "Lora gateway";
var gatewayGroupName = null; // If gatewayGroupName is not null - created device will be added to the entity group with such name.

if (data.cmd == "gw") {
    foreach( gatewayInfo : data.gws ) {
        var gatewayInfoMsg = {
            deviceName: gatewayDeviceNamePrefix + gatewayInfo.gweui,
            deviceType: gatewayDeviceType,
            telemetry: [{
                "ts": parseDateToTimestamp(gatewayInfo.ts),
                "values": getDataList(gatewayInfo, includeGatewayInfo)
            }],
            attributes: {
                eui: gatewayInfo.gweui
            }
        };
        addAdditionalInfoForDeviceMsg(gatewayInfoMsg, customerName, gatewayGroupName);
        uplinkDataList.add(gatewayInfoMsg);
    }
}

return uplinkDataList;

function addAdditionalInfoForDeviceMsg(deviceInfo, customerName, groupName) {
    if (customerName != null) {
        deviceInfo.customerName = customerName;
    }
    if (groupName != null) {
        deviceInfo.groupName = groupName;
    }
}

function parseDateToTimestamp(dateString) {
    var timestamp = -1;
    if (dateString != null) {

        timestamp = new Date(dateString).getTime();
        if (timestamp == -1) {
            var secondsSeparatorIndex = dateString.lastIndexOf('.') + 1;
            var millisecondsEndIndex = dateString.lastIndexOf('+');
            if (millisecondsEndIndex == -1) {
                millisecondsEndIndex = dateString.lastIndexOf('Z');
            }
            if (millisecondsEndIndex == -1) {
                millisecondsEndIndex = dateString.lastIndexOf('-');
            }
            if (millisecondsEndIndex == -1) {
                if (dateString.length >= secondsSeparatorIndex + 3) {
                    dateString = dateString.substring(0, secondsSeparatorIndex + 3);
                }
            } else {
                dateString = dateString.substring(0, secondsSeparatorIndex + 3) +
                dateString.substring(millisecondsEndIndex, dateString.length);
            }

            timestamp = new Date(dateString).getTime();
        }
    }
    // If we cannot parse timestamp - we will use the current timestamp
    if (timestamp == -1) {
        timestamp = Date.now();
    }

    return timestamp;
}

function processTelemetryData(telemetry, addDataToTelemetry) {
    if (telemetry.size > 1) {
        telemetry = addDataToMultipleTelemetries(telemetry, addDataToTelemetry);
    }
    else if (telemetry.size == 1) {
        telemetry = addDataToSingleTelemetry(telemetry, addDataToTelemetry);
    }
    else {
        telemetry.add(addDataToTelemetry);
    }

    return telemetry;
}

function addDataToMultipleTelemetries(telemetry, addDataToTelemetry) {
    foreach(element : addDataToTelemetry.entrySet()) {
        if(!telemetry[0]["values"].keys.contains(element.key)) {
            telemetry[0]["values"][element.key] = element.value;
        }
        if (!telemetry[1]["values"].keys.contains(element.key)) {
            telemetry[1]["values"][element.key] = element.value;
        }
    }

    return telemetry;
}

function addDataToSingleTelemetry(telemetry, addDataToTelemetry) {
    foreach(element : addDataToTelemetry.entrySet()) {
            if(!telemetry[0]["values"].keys.contains(element.key)) {
                telemetry[0]["values"][element.key] = element.value;
            }
    }

    return telemetry;
}

function readProtocolVersion(bytes) {
    var major = (bytes & 0xf0) >> 4;
    var minor = bytes & 0x0f;
    return "v" + major + "." + minor;
}

function readHardwareVersion(bytes) {
    var major = bytes[0] & 0xff;
    var minor = (bytes[1] & 0xff) >> 4;
    return "v" + major + "." + minor;
}

function readFirmwareVersion(bytes) {
    var major = bytes[0] & 0xff;
    var minor = bytes[1] & 0xff;
    return "v" + major + "." + minor;
}

function readCurrentAlarm(type) {
    var alarm = [];
    if ((type >> 0 & 0x01) != 0) {
        alarm.add("threshold alarm");
    }
    if ((type >> 1 & 0x01) != 0) {
        alarm.add("threshold alarm release");
    }
    if ((type >> 2 & 0x01) != 0) {
        alarm.add("over range alarm");
    }
    if ((type >> 3 & 0x01) != 0) {
        alarm.add("over range alarm release");
    }

    return alarm;
}

function readTemperatureAlarm(type) {
    var alarms = [];

    if ((type & 0x01) != 0) {
        alarms.add("Threshold alarm");
    }
    if ((type & 0x02) != 0) {
        alarms.add("Threshold alarm dismiss");
    }

    return alarms;
}
