var data = decodeToJson(payload);
var deviceName = "EM410-RDL " + data.EUI;
var deviceType = "EM410-RDL";
var groupName = null; // If groupName is not null - created device will be added to the entity group with such name.
var customerName = null; // If customerName is not null - created devices will be assigned to customer with such name. 

// use assetName and assetType instead of deviceName and deviceType
// to automatically create assets instead of devices.
// var assetName = 'Asset A';
// var assetType = 'building';

// If you want to parse incoming data somehow, you can add your code to this function.
// input: bytes
// expected output:
//  {
//    "attributes": {"attributeKey": "attributeValue"},
//    "telemetry": {"telemetryKey": "telemetryValue"}
//  }

function decodePayload(input) {
    var output = { attributes: {}, telemetry: []};

    var decoded = {};
    var fPort = data.port;
    var historyDataList = [];
    if(fPort == 85) {
        for(var i = 0; i < input.length - 2; ) {
            var channel_id = input[i++] & 0xff;
            var channel_type = input[i++] & 0xff;

            // DEVICE STATUS
            if (channel_id === 0xff && channel_type === 0x0b) {
                output.attributes.device_status = input[i];
                i += 1;
            }
            // IPSO VERSION
            else if (channel_id === 0xff && channel_type === 0x01) {
                output.attributes.ipso_version = readProtocolVersion(input[i]);
                i += 1;
            }
            // SERIAL NUMBER
            else if (channel_id === 0xff && channel_type === 0x16) {
                output.attributes.sn = readSerialNumber(input.slice(i, i + 8));
                i += 8;
            }
            // HARDWARE VERSION
            else if (channel_id === 0xff && channel_type === 0x09) {
                output.attributes.hardware_version = readHardwareVersion(input.slice(i, i + 2));
                i += 2;
            }
            // FIRMWARE VERSION
            else if (channel_id === 0xff && channel_type === 0x0a) {
                output.attributes.firmware_version = readFirmwareVersion(input.slice(i, i + 2));
                i += 2;
            }
            // LORAWAN CLASS TYPE
            else if (channel_id === 0xff && channel_type === 0x0f) {
                output.attributes.lorawan_class = readLoRaWANClass(input[i]);
                i += 1;
            }
            // TSL VERSION
            else if (channel_id === 0xff && channel_type === 0xff) {
                output.attributes.tsl_version = readTslVersion(input.slice(i, i + 2));
                i += 2;
            }
            // DEVICE RESET EVENT
            else if (channel_id === 0xff && channel_type === 0xfe) {
                output.attributes.reset_event = 1;
                i += 1;
            }
            // BATTERY
            else if (channel_id === 0x01 && channel_type === 0x75) {
                decoded.battery = input[i];
                i += 1;
            }
            // TEMPERATURE
            else if (channel_id === 0x03 && channel_type === 0x67) {
                decoded.temperature = parseBytesToInt(input, i, 2, false) / 10;
                i += 2;
            }
            // DISTANCE
            else if (channel_id === 0x04 && channel_type === 0x82) {
                decoded.distance = parseBytesToInt(input, i, 2, false);
                i += 2;
            }
            // POSITION
            else if (channel_id === 0x05 && channel_type === 0x00) {
                decoded.position = readPositionStatus(input[i]);
                i += 1;
            }
            // RADAR SIGNAL STRENGTH
            else if (channel_id === 0x06 && channel_type === 0xc7) {
                decoded.radar_signal_rssi = parseBytesToInt(input, i, 2, false) / 100;
                i += 2;
            }
            // DISTANCE ALARM
            else if (channel_id === 0x84 && channel_type === 0x82) {
                decoded.distance = parseBytesToInt(input, i, 2, false)
                decoded.distance_alarm = readDistanceAlarm(input[i + 2]);
                i += 3;
            }
            // DISTANCE MUTATION ALARM
            else if (channel_id === 0x94 && channel_type === 0x82) {
                decoded.distance = parseBytesToInt(input, i, 2, false);
                decoded.distance_mutation = parseBytesToInt(input, i + 2, 2, false);
                decoded.distance_alarm = readDistanceAlarm(input[i + 4]);
                i += 5;
            }
            // DISTANCE EXCEPTION ALARM
            else if (channel_id === 0xb4 && channel_type === 0x82) {
                var distance_raw_data = parseBytesToInt(input, i, 2, false);
                var distance_value = parseBytesToInt(input, i, 2, false);
                decoded.distance_exception = readDistanceException(input[i + 2]);
                i += 3;

                var data = {};
                if (distance_raw_data === 0xfffd || distance_raw_data === 0xffff) {
                    // IGNORE NO TARGET AND SENSOR EXCEPTION
                } else {
                    data.distance = distance_value;
                }
            }
            // HISTORY
            else if (channel_id === 0x20 && channel_type === 0xce) {
                var historyData = {
                    ts: parseBytesToInt(input, i, 4, false) * 1000,
                    values: {}
                };

                var distance_raw_data = parseBytesToInt(input, i + 4, 2, false);
                var distance_value = parseBytesToInt(input, i + 4, 2, false);
                var temperature_raw_data = parseBytesToInt(input, i + 6, 2, false);
                var temperature_value = parseBytesToInt(input, i + 6, 2, false) / 10;
                var mutation = parseBytesToInt(input, i + 8, 2, false);
                var event_value = input[i + 10];
                i += 11;

                var data = {}
                if (distance_raw_data === 0xfffd) {
                    historyData["values"].distance_exception = "No Target";
                } else if (distance_raw_data === 0xffff) {
                    historyData["values"].distance_exception = "Sensor Exception";
                } else if (distance_raw_data === 0xfffe) {
                    historyData["values"].distance_exception = "Disabled";
                } else {
                    historyData["values"].distance = distance_value;
                }

                if (temperature_raw_data === 0xfffe) {
                    historyData["values"].temperature_exception = "Disabled";
                } else if (temperature_raw_data === 0xffff) {
                    historyData["values"].temperature_exception = "Sensor Exception";
                } else {
                    historyData["values"].temperature = temperature_value;
                }

                var event = readHistoryEvent(event_value);
                if (event.length > 0) {
                    historyData["values"].event = event;
                }
                if (event.contains("Mutation Alarm")) {
                    historyData["values"].distance_mutation = mutation;
                }

                historyDataList.add(historyData);
            }
        }
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    output.telemetry.addAll(historyDataList);

    // --- Decoding code --- //
    return output;
}

// --- attributes and telemetry objects ---
var telemetry = [];
var attributes = {};
// --- attributes and telemetry objects ---

// --- Timestamp parsing
timestamp = data.ts;
// --- Timestamp parsing

// Message parsing
// To avoid paths in the decoded objects we passing false value to function as "pathInKey" argument.
// Warning: pathInKey can cause already found fields to be overwritten with the last value found.

var uplinkDataList = [];

// Passing incoming bytes to decodePayload function, to get custom decoding
var customDecoding = decodePayload(hexToBytes(data.data));

// Collecting data to result
if (customDecoding.?telemetry.size() > 0) {
    if (customDecoding.telemetry instanceof java.util.ArrayList) {
        foreach(telemetryObj: customDecoding.telemetry) {
            if (telemetryObj.ts != null && telemetryObj.values != null) {
                telemetry.add(telemetryObj);
            }
        }
    } else {
        telemetry.putAll(customDecoding.telemetry);
    }
}

if (customDecoding.?attributes.size() > 0) {
    attributes.putAll(customDecoding.attributes);
}

// You can add some keys manually to attributes or telemetry
attributes.eui = data.EUI;
attributes.fPort = data.port;
attributes.frequency = data.freq;

var isIncludeGatewayInfo = metadata["includeGatewayInfo"];
if(isIncludeGatewayInfo == true) {
    var addDataToTelemetry = {};
    addDataToTelemetry.rssi = data.rssi;
    addDataToTelemetry.seqno = data.seqno;
    addDataToTelemetry.snr = data.snr;
    addDataToTelemetry.ack = data.ack;
    addDataToTelemetry.toa = data.toa;
    addDataToTelemetry.fCnt = data.fcnt;

   telemetry = processTelemetryData(telemetry, addDataToTelemetry);
}

var deviceInfo = {
    deviceName: deviceName,
    deviceType: deviceType,
//  assetName: assetName,
//  assetType: assetType,
    attributes: attributes,
    telemetry: telemetry, 
};

addAdditionalInfoForDeviceMsg(deviceInfo, customerName, groupName);

uplinkDataList.add(deviceInfo);

var gatewayDeviceNamePrefix = "Gateway ";
var gatewayDeviceType = "Lora gateway";
var gatewayGroupName = null; // If gatewayGroupName is not null - created device will be added to the entity group with such name.

if (data.cmd == "gw") {
    foreach( gatewayInfo : data.gws ) {
        var addGatewayInfo = {};

        // You can add some keys manually telemetry
        addGatewayInfo.rssi = gatewayInfo.rssi;
        addGatewayInfo.snr = gatewayInfo.snr;
        // You can add some keys manually telemetry

        var gatewayInfoMsg = {
            deviceName: gatewayDeviceNamePrefix + gatewayInfo.gweui,
            deviceType: gatewayDeviceType,
            telemetry: [{
                "ts": gatewayInfo.ts,
                "values": addGatewayInfo
            }],
            attributes: {
                eui: gatewayInfo.gweui
            }
        };
        addAdditionalInfoForDeviceMsg(gatewayInfoMsg, customerName, gatewayGroupName);
        uplinkDataList.add(gatewayInfoMsg);
    }
}

return uplinkDataList;

function addAdditionalInfoForDeviceMsg(deviceInfo, customerName, groupName) {
    if (customerName != null) {
        deviceInfo.customerName = customerName;
    }
    if (groupName != null) {
        deviceInfo.groupName = groupName;
    }
}

function processTelemetryData(telemetry, addDataToTelemetry) {
    if (telemetry.size >= 1) {
        telemetry = addDataToTelemetries(telemetry, addDataToTelemetry);
    }
    else {
        telemetry.add(addDataToTelemetry);
    }

    return telemetry;
}

function addDataToTelemetries(telemetries, addDataToTelemetry) {
    foreach(telemetry : telemetries) {
        foreach(element : addDataToTelemetry.entrySet()) {
            if(!telemetry["values"].keys.contains(element.key)) {
                telemetry["values"][element.key] = element.value;
            }
        } 
    }

    return telemetries;
}

function readProtocolVersion(bytes) {
    var major = (bytes & 0xf0) >> 4;
    var minor = bytes & 0x0f;
    return "v" + major + "." + minor;
}

function readHardwareVersion(bytes) {
    var major = bytes[0] & 0xff;
    var minor = (bytes[1] & 0xff) >> 4;
    return "v" + major + "." + minor;
}

function readFirmwareVersion(bytes) {
    var major = bytes[0] & 0xff;
    var minor = bytes[1] & 0xff;
    return "v" + major + "." + minor;
}

function readTslVersion(bytes) {
    var major = bytes[0] & 0xff;
    var minor = bytes[1] & 0xff;
    return "v" + major + "." + minor;
}

function readSerialNumber(bytes) {
    var resultSerialNumber = "";

    for (b : bytes) {
        resultSerialNumber += String.format("%02x", b & 0xff);
    }

    return resultSerialNumber;
}

function readLoRaWANClass(type) {
    switch (type) {
        case 0:
            return "ClassA";
        case 1:
            return "ClassB";
        case 2:
            return "ClassC";
        case 3:
            return "ClassCtoB";
    }
}

function readPositionStatus(status) {
    switch (status) {
        case 0:
            return "Normal";
        case 1:
            return "Tilt";
        default:
            return "Unknown";
    }
}

function readDistanceAlarm(status) {
    switch (status) {
        case 0:
            return "Threshold Alarm Release";
        case 1:
            return "Threshold Alarm";
        case 2:
            return "Mutation Alarm";
        default:
            return "Unknown";
    }
}

function readDistanceException(status) {
    switch (status) {
        case 0:
            return "Blind Spot Alarm Release";
        case 1:
            return "Blind Spot Alarm";
        case 2:
            return "No Target";
        case 3:
            return "Sensor Exception";
        default:
            return "Unknown";
    }
}

function readHistoryEvent(status) {
    var event = [];

    if (((status >>> 0) & 0x01) === 0x01) {
        event.push("Threshold Alarm");
    }
    if (((status >>> 1) & 0x01) === 0x01) {
        event.push("Threshold Alarm Release");
    }
    if (((status >>> 2) & 0x01) === 0x01) {
        event.push("Blind Spot Alarm");
    }
    if (((status >>> 3) & 0x01) === 0x01) {
        event.push("Blind Spot Alarm Release");
    }
    if (((status >>> 4) & 0x01) === 0x01) {
        event.push("Mutation Alarm");
    }
    if (((status >>> 5) & 0x01) === 0x01) {
        event.push("Tilt Alarm");
    }

    return event;
}
