function decodeFrmPayload(input) {
    var output = { attributes: {}, telemetry: []};

    // --- Decoding code --- //
    var decoded = {};
    var historyDataList = [];
    for (var i = 0; i < input.length - 2 ; ) {
        var channel_id = input[i++] & 0xff;
        var channel_type = input[i++] & 0xff;

       // BATTERY
        if (channel_id === 0x01 && channel_type === 0x75) {
            decoded.battery = input[i];
            i += 1;
        }
        // TEMPERATURE
        else if (channel_id === 0x03 && channel_type === 0x67) {
            decoded.temperature = parseBytesToInt(input, i, 2, false) / 10;
            i += 2;

            // â„‰
            // decoded.temperature = parseBytesToInt(input, i, 2, false) / 10 * 1.8 + 32;
            // i +=2;
        }
        // HUMIDITY
        else if (channel_id === 0x04 && channel_type === 0x68) {
            decoded.humidity = parseBytesToInt(input, i, 1, false) / 2;
            i += 1;
        }
        // CO2
        else if (channel_id === 0x05 && channel_type === 0x7d) {
            decoded.co2 = parseBytesToInt(input, i, 2, false);
            i += 2;
        }
        // PRESSURE
        else if (channel_id === 0x06 && channel_type === 0x73) {
            decoded.pressure = parseBytesToInt(input, i, 2, false) / 10;
            i += 2;
        }
        // TEMPERATURE CHANGE ALARM
        else if (channel_id === 0x83 && channel_type === 0xd7) {
            decoded.temperature = parseBytesToInt(input, i, 2, false) / 10;
            decoded.temperature_change = parseBytesToInt(input, i + 2, 2, false) / 10;
            decoded.temperature_alarm = readTemperatureAlarm(input[i + 4]);
            i += 5;
        }
        // HISTROY
        else if (channel_id === 0x20 && channel_type === 0xce) {
            var historyData = {
                ts: parseBytesToInt(input, i, 4, false) * 1000,
                values: {
                    co2: parseBytesToInt(input, i + 4, 2, false),
                    pressure: parseBytesToInt(input, i + 6, 1, false) / 10,
                    temperature: parseBytesToInt(input, i + 8, 2, false) / 10,
                    humidity: parseBytesToInt(input, i + 10, 1, false, false) / 2
                }
            };
            i += 11;

            historyDataList.add(historyData);
        } 
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    // --- Decoding code --- //
    return output;
}

