function decodeFrmPayload(input) {
    var output = {
        attributes: {}, 
        telemetry: {}
    };

    // --- Decoding code --- //
    var decoded = {};
    var fPort = data.uplink_message.f_port;
    var historyDataList = [];
    if(fPort == 85) {
        for(var i = 0; i < input.length - 2; ) {
            var channel_id = input[i++] & 0xff;
            var channel_type = input[i++] & 0xff;

            // PROTOCOL VESION
            if (channel_id === 0xff && channel_type === 0x01) {
                output.attributes.protocol_version = input[i];
                i += 1;
            }
            // SERIAL NUMBER
            else if (channel_id === 0xff && channel_type === 0x16) {
                output.attributes.sn = readSerialNumber(java.util.Arrays.copyOfRange(input, i, i + 8));
                i += 8;
            }
            // HARDWARE VERSION
            else if (channel_id === 0xff && channel_type === 0x09) {
                output.attributes.hardware_version = readVersion(java.util.Arrays.copyOfRange(input, i, i + 2));
                i += 2;
            }
            // FIRMWARE VERSION
            else if (channel_id === 0xff && channel_type === 0x1f) {
                output.attributes.firmware_version = readVersion(java.util.Arrays.copyOfRange(input, i, i + 4));
                i += 4;
            }
            // TOTAL COUNTER IN
            else if (channel_id === 0x03 && channel_type === 0xd2) {
                decoded.total_counter_in = parseBytesToInt(input, i, 4, false);
                i += 4;
            }
            // TOTAL COUNTER OUT
            else if (channel_id === 0x04 && channel_type === 0xd2) {
                decoded.total_counter_out = parseBytesToInt(input, i, 4, false);
                i += 4;
            }
            // PERIODIC COUNTER
            else if (channel_id === 0x05 && channel_type === 0xcc) {
                decoded.periodic_counter_in = parseBytesToInt(input, i, 2, false);
                decoded.periodic_counter_out = parseBytesToInt(input, i + 2, 2, false);
                i += 4;
            }
        }
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    output.telemetry.addAll(historyDataList);

    // --- Decoding code --- //
    return output;
}


