attributes.frequency = data.uplink_message.?settings.?frequency;


var gatewayInfo = getGatewayInfo();
var addDataToTelemetry = {};
addDataToTelemetry.snr = gatewayInfo.snr;
addDataToTelemetry.rssi = gatewayInfo.rssi;
