// If you want to parse incoming data somehow, you can add your code to this function.
// input: bytes
// expected output:
//  {
//    "attributes": {"attributeKey": "attributeValue"},
//    "telemetry": [{"ts": 1...1, "values": {"telemetryKey":"telemetryValue"}, {"ts": 1...2, "values":              {"telemetryKey":"telemetryValue"}}]
//  }

function decodeFrmPayload(input) {
    var output = {
        attributes: {}, 
        telemetry: {}
    };

    // --- Decoding code --- //
    var decoded = {};
    var fPort = data.uplink_message.f_port;
    var historyDataList = [];
    if(fPort == 85) {
        for(var i = 0; i < input.length - 2; ) {
            var channel_id = input[i++] & 0xff;
            var channel_type = input[i++] & 0xff;

            // DEVICE STATUS
            if (channel_id === 0xff && channel_type === 0x0b) {
                output.attributes.device_status = "on";
                i += 1;
            }
            // IPSO VERSION
            else if (channel_id === 0xff && channel_type === 0x01) {
                output.attributes.ipso_version = readProtocolVersion(input[i]);
                i += 1;
            }
            // SERIAL NUMBER
            else if (channel_id === 0xff && channel_type === 0x16) {
                output.attributes.sn = readSerialNumber(java.util.Arrays.copyOfRange(input, i, i + 8));
                i += 8;
            }
            // HARDWARE VERSION
            else if (channel_id === 0xff && channel_type === 0x09) {
                output.attributes.hardware_version = readHardwareVersion(java.util.Arrays.copyOfRange(input, i, i + 2));
                i += 2;
            }
            // FIRMWARE VERSION
            else if (channel_id === 0xff && channel_type === 0x0a) {
                output.attributes.firmware_version = readFirmwareVersion(java.util.Arrays.copyOfRange(input, i, i + 2));
                i += 2;
            }
            // LORAWAN CLASS TYPE
            else if (channel_id === 0xff && channel_type === 0x0f) {
                output.attributes.lorawan_class = readLoRaWANType(input[i]);
                i += 1;
            }
            // TSL VERSION
            else if (channel_id === 0xff && channel_type === 0xff) {
                output.attributes.tsl_version = readTslVersion(java.util.Arrays.copyOfRange(input, i, i + 2));
                i += 2;
            }
            // BATTERY(MAIN)
            else if (channel_id === 0x01 && channel_type === 0x75) {
                decoded.battery_main = input[i];
                i += 1;
            }
            // BATTERY(NODE)
            else if (channel_id === 0x02 && channel_type === 0x75) {
                decoded.battery_node = input[i];
                i += 1;
            }
            // EVENT
            else if (channel_id === 0x03 && channel_type === 0xf4) {
                var data = {};
                data.type = readEventType(input[i]);
                data.status = readEventStatus(input[i + 1]);

                decoded.event.add(data);
                i += 2;
            }
            // TOTAL IN / OUT
            else if (channel_id === 0x04 && channel_type === 0xcc) {
                decoded.total_in = parseBytesToLong(input, i, 2, false)
                decoded.total_out = parseBytesToLong(input, i + 2, 2, false)
                i += 4;
            }
            // PERIOD IN / OUT
            else if (channel_id === 0x05 && channel_type === 0xcc) {
                decoded.period_in = parseBytesToLong(input, i, 2, false)
                decoded.period_out = parseBytesToInt(input, i + 2, 2, false)
                i += 4;
            }
            // TOTAL IN / OUT ALARM
            else if (channel_id === 0x84 && channel_type === 0xcc) {
                decoded.total_in = parseBytesToLong(input, i, 2, false);
                decoded.total_out = parseBytesToLong(input, i + 2, 2, false);
                decoded.total_count_alarm = readAlarmType(input[i + 4]);
                i += 5;
            }
            // PERIOD IN / OUT ALARM
            else if (channel_id === 0x85 && channel_type === 0xcc) {
                decoded.period_in = parseBytesToLong(input, i, 2, false);
                decoded.period_out = parseBytesToLong(input, i + 2, 2, false);
                decoded.period_count_alarm = readAlarmType(input[i + 4]);
                i += 5;
            }
            // HISTORICAL DATA
            else if (channel_id === 0x20 && channel_type === 0xce) {
                var historyData = {
                    ts: parseBytesToInt(input, i, 4, false) * 1000,
                };

                var type = input[i + 4];
                // historical data without total in/out
                if (type === 0) {
                    var historyDataValue = {
                        period_in: parseBytesToInt(input, i + 5, 2, false),
                        period_out: parseBytesToInt(input, i + 7, 2, false)
                    };

                    i += 9;
                }
                // historical data with total in/out
                else if (type === 1) {
                     var historyDataValue = {
                        period_in: parseBytesToInt(input, i + 5, 2, false),
                        period_out: parseBytesToInt(input, i + 7, 2, false),
                        total_in: parseBytesToInt(input, i + 9, 2, false),
                        total_out: parseBytesToInt(input, i + 11, 2, false)
                    }; 

                    i += 13;
                }

                historyData["values"] = historyDataValue;
                historyDataList.add(historyData);
            }
        }
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    output.telemetry.addAll(historyDataList);

    // --- Decoding code --- //
    return output;
}


