function readD2DMasterConfig(bytes) {
    var offset = 0;
    var config = {};
    config.mode = readD2DMode(bytes[offset]);
    config.enable = readEnableStatus(bytes[offset + 1]);
    config.lora_uplink_enable = readEnableStatus(bytes[offset + 2]);
    config.d2d_cmd = readD2DCommand(java.util.Arrays.copyOfRange(bytes, offset + 3, offset + 5));
    config.time = parseBytesToInt(bytes, offset + 5, 2);
    config.time_enable = readEnableStatus(bytes[offset + 7]);
    return config;
}

function readD2DCommand(bytes) {
     if (bytes == null || bytes.length < 2) {
        return null;
    }

    var hex1 = String.format("%02x", bytes[1] & 0xFF);
    var hex0 = String.format("%02x", bytes[0] & 0xFF);

    return hex1 + hex0;
}

function readD2DMode(type) {
    switch (type) {
        case 0:
            return "occupied";
        case 1:
            return "vacant";
        case 2:
            return "bright";
        case 3:
            return "dim";
        case 4:
            return "occupied_bright";
        case 5:
            return "occupied_dim";
        default:
            return null;
    }
}

function readHibernateConfig(bytes) {
    var offset = 0;
    var config = {};
    config.id = bytes[offset] + 1;
    config.enable = readEnableStatus(bytes[offset + 1]);
    config.start_time = parseBytesToInt(bytes, offset + 2, 2);
    config.end_time = parseBytesToInt(bytes, offset + 4, 2);
    return config;
}

function readHexString(bytes) {
    var result = "";
    for (var i = 0; i < bytes.length; i++) {
        result += String.format("%02x", bytes[i] & 0xFF);
    }
    return result;
}

function handle_downlink_response(channel_type, bytes, offset) {
    var decoded = {};

    switch (channel_type) {
        case 0x10:
            decoded.reboot = readYesNoStatus(1);
            offset += 1;
            break;
        case 0x28:
            decoded.report_status = readReportType(bytes[offset]);
            offset += 1;
            break;
        case 0x35:
            decoded.d2d_key = readHexString(java.util.Arrays.copyOfRange(bytes, offset, offset + 8));
            offset += 8;
            break;
        case 0x4a:
            decoded.sync_time = readYesNoStatus(1);
            offset += 1;
            break;
        case 0x84:
            decoded.d2d_enable = readEnableStatus(bytes[offset]);
            offset += 1;
            break;
        case 0x8e:
            // ignore the first byte
            decoded.report_interval = parseBytesToInt(bytes, offset + 1, 2);
            offset += 3;
            break;
        case 0x8f:
            decoded.bluetooth_enable = readEnableStatus(bytes[offset]);
            offset += 1;
            break;
        case 0x96:
            var d2d_master_config = readD2DMasterConfig(java.util.Arrays.copyOfRange(bytes, offset, offset + 8));
            offset += 8;
            decoded.d2d_master_config = decoded.d2d_master_config != null ? decoded.d2d_master_config : [];
            decoded.d2d_master_config.push(d2d_master_config);
            break;
        case 0xba:
            decoded.dst_config = readDstConfig(java.util.Arrays.copyOfRange(bytes, offset, offset + 10));
            offset += 10;
            break;
        case 0xbd:
            decoded.time_zone = readTimeZone(parseBytesToInt(bytes, offset, 2));
            offset += 2;
            break;
        default:
            break;
    }

    return { data: decoded, offset: offset };
}

function handle_downlink_response_ext(code, channel_type, bytes, offset) {
    var decoded = {};

    switch (channel_type) {
        case 0x3e:
            decoded.pir_sensitivity = readPirSensitivity(bytes[offset]);
            offset += 1;
            break;
        case 0x3f:
            decoded.radar_sensitivity = readRadarSensitivity(bytes[offset]);
            offset += 1;
            break;
        case 0x40:
            decoded.pir_idle_interval = bytes[offset];
            offset += 1;
            break;
        case 0x41:
            decoded.pir_illuminance_threshold = {};
            decoded.pir_illuminance_threshold.enable = readEnableStatus(bytes[offset]);
            decoded.pir_illuminance_threshold.upper_limit = parseBytesToInt(bytes, offset + 1, 2);
            decoded.pir_illuminance_threshold.lower_limit =  parseBytesToInt(bytes, offset + 3, 2);
            offset += 5;
            break;
        case 0x42:
            decoded.pir_window_time = readPirWindowTime(bytes[offset]);
            offset += 1;
            break;
        case 0x43:
            decoded.pir_pulse_times = readPirPulseTimes(bytes[offset]);
            offset += 1;
            break;
        case 0x44:
            var hibernate_config = readHibernateConfig(java.util.Arrays.copyOfRange(bytes, offset, offset + 6));
            offset += 6;
            decoded.hibernate_config = decoded.hibernate_config != null ? decoded.hibernate_config : [];
            decoded.hibernate_config.push(hibernate_config);
            break;
        default:
            break;
    }

    if (hasResultFlag(code)) {
        var result_value = bytes[offset];
        offset += 1;

        if (result_value != 0) {
            var request = decoded;
            decoded = {};
            decoded.device_response_result = {};
            decoded.device_response_result.channel_type = channel_type;
            decoded.device_response_result.result = readResultStatus(result_value);
            decoded.device_response_result.request = request;
        }
    }

    return { data: decoded, offset: offset };
}
