// result.group = 'SensorsGroup'; // If group is not null - created entity will be added to the entity group with such name.

// Return the final result object.
return result;

/**
 * Parse a slice of bytes from an array into an integer (big-endian).
 *
 * @param {number[]} input - The array of bytes.
 * @param {number} offset - The starting index.
 * @param {number} length - The number of bytes to convert.
 * @returns {number} - The resulting integer.
 */
function parseBytesToInt(input, offset, length) {
  var result = 0;
  for (var i = offset; i < offset + length; i++) {
    result = (result << 8) | (input[i] & 0xFF);
  }
  return result;
}

configuration.tbelDecoder:
/**
 * Decodes the incoming payload and returns a structured object containing telemetry data and attributes.
 *
 * @param {byte[]} input - The raw payload received as an array of bytes.
 * @returns {Object} output - The structured output with decoded telemetry and attributes.
 */

function decodePayload(input) {
    // Initialize the output object with empty attributes and telemetry for clarity.
    var result = { attributes: {}, telemetry: {}};


    // Extract the timestamp from metadata (represented in milliseconds).
    var timestamp = metadata.ts; // ts is the timestamp parsed from the incoming message's time, or returns the current time if it cannot be parsed.

    var values = {};
    for (var i = 0; i < input.length; ) {
        var channel_id = input[i++] & 0xff;
        var channel_type = input[i++] & 0xff;
        // IPSO VERSION
        if (channel_id === 0xff && channel_type === 0x01) {
            values.ipso_version = readProtocolVersion(input[i]);
            i += 1;
        }
        // HARDWARE VERSION
        else if (channel_id === 0xff && channel_type === 0x09) {
            values.hardware_version = readHardwareVersion(input.slice(i, i + 2));
            i += 2;
        }
        // FIRMWARE VERSION
        else if (channel_id === 0xff && channel_type === 0x0a) {
            values.firmware_version = readFirmwareVersion(input.slice(i, i + 2));
            i += 2;
        }
        // TSL VERSION
        else if (channel_id === 0xff && channel_type === 0xff) {
            values.tsl_version = readTslVersion(input.slice(i, i + 2));
            i += 2;
        }
        // SERIAL NUMBER
        else if (channel_id === 0xff && channel_type === 0x16) {
            values.sn = readSerialNumber(input.slice(i, i + 8));
            i += 8;
        }
        // LORAWAN CLASS TYPE
        else if (channel_id === 0xff && channel_type === 0x0f) {
            values.lorawan_class = readLoRaWANClass(input[i]);
            i += 1;
        }
        // RESET EVENT
        else if (channel_id === 0xff && channel_type === 0xfe) {
            values.reset_event = readResetEvent(1);
            i += 1;
        }
        // DEVICE STATUS
        else if (channel_id === 0xff && channel_type === 0x0b) {
            values.device_status = readDeviceStatus(1);
            i += 1;
        }
        // BATTERY
        if (channel_id === 0x01 && channel_type === 0x75) {
            values.battery = input[i];
            i += 1;
        }
        // PIR
        else if (channel_id === 0x03 && channel_type === 0x00) {
            values.pir = input[i] === 0 ? "normal" : "trigger";
            i += 1;
        }
        // DAYLIGHT
        else if (channel_id === 0x04 && channel_type === 0x00) {
            values.daylight = input[i] === 0 ? "dark" : "light";
            i += 1;
        }
    }

    // Combine the timestamp with values and add it to the telemetry.
    result.telemetry = {
        ts: timestamp,
        values: values
    };

    // Return the fully constructed output object.
    return result;
}

var uplinkDataResult = [];

var result = decodePayload(payload);

uplinkDataResult.push(result);

var gatewayDeviceNamePrefix = "Gateway ";
var gatewayDeviceType = "Lora gateway";

if (metadata.cmd == "gw") {
    foreach( gatewayInfo : metadata.gws ) {
        var addGatewayInfo = {};

        // You can add some keys manually telemetry
        addGatewayInfo.rssi = gatewayInfo.rssi;
        addGatewayInfo.snr = gatewayInfo.snr;
        // You can add some keys manually telemetry

        var gatewayInfoMsg = {
            name: gatewayDeviceNamePrefix + gatewayInfo.gweui,
            profile: gatewayDeviceType,
            telemetry: {
                "ts": gatewayInfo.ts,
                "values": addGatewayInfo
            },
            attributes: {
                eui: gatewayInfo.gweui
            }
        };
        uplinkDataResult.add(gatewayInfoMsg);
    }
}

// Return the final result object.
return uplinkDataResult;

function readProtocolVersion(bytes) {
    var major = (bytes & 0xf0) >> 4;
    var minor = bytes & 0x0f;
    return "v" + major + "." + minor;
}

function readHardwareVersion(bytes) {
    var major = bytes[0] & 0xff;
    var minor = (bytes[1] & 0xff) >> 4;
    return "v" + major + "." + minor;
}

function readFirmwareVersion(bytes) {
    var major = bytes[0] & 0xff;
    var minor = bytes[1] & 0xff;
    return "v" + major + "." + minor;
}

function readTslVersion(bytes) {
    var major = bytes[0] & 0xff;
    var minor = bytes[1] & 0xff;
    return "v" + major + "." + minor;
}

function readSerialNumber(bytes) {
    var result = "";
    for (int i = 0; i < bytes.length; i++) {
        var hex = Integer.toHexString(bytes[i] & 0xFF);
        if (hex.length() == 1) {
            hex = "0" + hex;
        }
        result += hex;
    }
    return result;
}

function readLoRaWANClass(type) {
    switch (type) {
        case 0: return "Class A";
        case 1: return "Class B";
        case 2: return "Class C";
        case 3: return "Class CtoB";
        default: return null;
    }
}

function readResetEvent(status) {
    switch (status) {
        case 0: return "normal";
        case 1: return "reset";
        default: return null;
    }
}

function readDeviceStatus(status) {
    switch (status) {
        case 0: return "off";
        case 1: return "on";
        default: return null;
    }
}

function readYesNoStatus(status) {
    switch (status) {
        case 0: return "no";
        case 1: return "yes";
        default: return null;
    }
}

function readPirStatus(status) {
    switch (status) {
        case 0: return "normal";
        case 1: return "trigger";
        default: return null;
    }
}

function readDaylight(status) {
    switch (status) {
        case 0: return "dim";
        case 1: return "bright";
        default: return null;
    }
}
