configuration.tbelEncoder:
// Encode downlink data from incoming Rule Engine message

// msg - JSON message payload downlink message json
// msgType - type of message, for ex. 'ATTRIBUTES_UPDATED', 'POST_TELEMETRY_REQUEST', etc.
// metadata - list of key-value pairs with additional data about the message
// integrationMetadata - list of key-value pairs with additional data defined in Integration executing this converter

/** Encoder **/


// "G" Uplink; "G/GH" Downlink === "g"
// "CL&CN" Downlink === "cl_cn"
var bitMaskWiringSettingss = [
    {"y1": 0, "g": 2, "ob": 4, "w1": 6},
    {"e": 0, "cl_cn": 2, "pek": 4, "w2_aux": 6},
    {"y2_gl": 0, "ob_mode": 2}
];

var data = bytesToBase64(milesightDeviceEncode(msg));

function milesightDeviceEncode(payload) {
    var encoded = [];
    if (payload.reboot != null) {
        encoded = encoded.concat(reboot(payload.reboot));
    }
    if (payload.report_status != null) {
        encoded = encoded.concat(reportStatus(payload.report_status));
    }
    if (payload.report_interval != null) {
        encoded = encoded.concat(setReportInterval(payload.report_interval));
    }
    if (payload.system_on_off != null) {
        encoded = encoded.concat(systemOnOff(payload.system_on_off));
    }
    if (payload.control_permissions != null) {
        encoded = encoded.concat(setControlPermissions(payload.control_permissions));
    }
    if (payload.?child_lock_config.size() > 0) {
        encoded = encoded.concat(setChildLock(payload.child_lock_config));
    }
    if (payload.timezone != null) {
        encoded = encoded.concat(setTimezone(payload.timezone));
    }
    if (payload.?dst_config.size() > 0) {
        encoded = encoded.concat(setDaylightSavingTime(payload.dst_config));
    }
    if (payload.?multicast_group_config.size() > 0) {
        encoded = encoded.concat(setMulticastGroupConfig(payload.multicast_group_config));
    }
    if (payload.?wiring_settings.size() > 0) {
        encoded = encoded.concat(setWiringSettings(payload.wiring_settings));
    }
    if (payload.ob_mode != null) {
        encoded = encoded.concat(setOBMode(payload.ob_mode));
    }
    if (payload.?freeze_protection_config.size() > 0) {
        encoded = encoded.concat(setFreezeProtection(payload.freeze_protection_config.enable, payload.freeze_protection_config.temperature));
    }
    if (payload.?temperature_control.size() > 0) {
        if (payload.temperature_control.temperature != null) {
            encoded = encoded.concat(setTemperatureTarget(payload.temperature_control.mode, payload.temperature_control.temperature));
        } else {
            encoded = encoded.concat(setTemperatureControlMode(payload.temperature_control.mode));
        }
    }
    if (payload.?temperature_tolerance.size() > 0) {
        encoded = encoded.concat(setTemperatureTolerance(payload.temperature_tolerance.temperature_error, payload.temperature_tolerance.auto_control_temperature_error));
    }
    if (payload.fan_mode != null) {
        encoded = encoded.concat(setFanMode(payload.fan_mode));
    }
    if (payload.?fan_delay.size() > 0) {
        encoded = encoded.concat(setFanModeWithDelay(payload.fan_delay.enable, payload.fan_delay.fan_delay_time));
    }
    if (payload.fan_circulate != null) {
        encoded = encoded.concat(setFanModeWithCirculate(payload.fan_circulate));
    }
    if (payload.?fan_regulate_humidity.size() > 0) {
        encoded = encoded.concat(setFanRegulateHumidity(payload.fan_regulate_humidity.enable, payload.fan_regulate_humidity.regulate_interval));
    }
    if (payload.?humidity_range.size() > 0) {
        encoded = encoded.concat(setHumidityRange(payload.humidity_range.min, payload.humidity_range.max));
    }
    if (payload.?dehumidify.size() > 0) {
        encoded = encoded.concat(setTemperatureDehumidify(payload.dehumidify.enable, payload.dehumidify.temperature_tolerance));
    }
    return encoded;
}

function reboot(reboot) {
    validateArrayValue("Reboot", reboot, [0, 1]);
    return reboot === 0 ? [] : [0xff, 0x10, 0xff];
}

function reportStatus(report_status) {
    validateArrayValue("Report status", report_status, [0, 1]); // 01, the device will return a periodic packet
    return [0xFF, 0x28, report_status];
}

function setReportInterval(report_interval) {
    isNumber("Reporting Interval", report_interval);
    return [0xFF, 0x8E, 0x00, report_interval, report_interval >>> 8];
}

function systemOnOff(system_on_off) {
    validateArrayValue("System On/Off", system_on_off, [0, 1]);
    return [0xFF, 0xC5, system_on_off];
}

function setControlPermissions(control_permissions) {
    validateArrayValue("Control Permission", control_permissions, [0, 1]);
    return [0xFF, 0xF6, control_permissions];
}

function setChildLock(child_lock_config) {
    var button_mask_bit_offset = {
        power_button: 0,
        up_button: 1,
        down_button: 2,
        fan_button: 3,
        mode_button: 4,
        reset_button: 5
    };
    var arrayChildLock = new int[6];
    foreach(button
:
    child_lock_config.entrySet()
)
    {
        validateArrayValue("Child_lock_config." + button.key, button.value, [0, 1]);
        var ind = button_mask_bit_offset[button.key];
        arrayChildLock[5 - ind] = button.value;
    }
    var intChildLock = parseBinaryArrayToInt(arrayChildLock);
    return [0xFF, 0x25, 0xFF, intChildLock];
}

function setTimezone(timezone) {
    isNumber("UTC Time Zone", timezone);
    var tz = valueToInt(timezone, 60);
    return [0xFF, 0xBD, tz & 0xFF, (tz >>> 8) & 0xFF];
}

function setDaylightSavingTime(dstConfig) {
    validateArrayValue("Dst_config.enable", dstConfig.enable, [0, 1]);
    isNumber("Dst_config.bias", dstConfig.bias);
    if (dstConfig.?start_time.size() > 0 && dstConfig.?end_time.size() > 0) {
        var rezDstConfig = [0xFF, 0xBA, 0, 0];
        if (dstConfig.enable === 0) {
            return rezDstConfig.concat([0, 0, 0, 0, 0, 0, 0, 0]);
        }
        rezDstConfig[2] = dstConfig.enable;
        rezDstConfig[3] = dstConfig.bias;
        var startDate = validateDstConfigDateTime(dstConfig.start_time.month, dstConfig.start_time.week, dstConfig.start_time.weekday, dstConfig.start_time.time, "Start");
        var endDate = validateDstConfigDateTime(dstConfig.end_time.month, dstConfig.end_time.week, dstConfig.end_time.weekday, dstConfig.end_time.time, "End");
        rezDstConfig = rezDstConfig.concat(startDate);
        return rezDstConfig.concat(endDate);
    }
}

function setMulticastGroupConfig(multicast_group_config) {
    var groupName;
    var groupValue;
    var bitArray = new int [8];
    for (var i = 1; i < 5; i++) {
        groupName = "group" + i + "_enable";
        groupValue = multicast_group_config[groupName];
        if (groupValue != null) {
            validateArrayValue(groupName, groupValue, [0, 1]);
            bitArray[4 - i] = 1;
            bitArray[8 - i] = groupValue;
        }
    }
    return [0xFF, 0x82, parseBinaryArrayToInt(bitArray)];
}

function setWiringSettings(wiring_settings) {
    var wiringSets = [0xFF, 0xCA];
    for (var i = 0; i < 3; i++) {
        var bitMaskWiringSettings = bitMaskWiringSettingss[i];
        var wiringSettingValue = 0;
        foreach(bitMaskWiringSetting
    :
        bitMaskWiringSettings.entrySet()
    )
        {
            if (wiring_settings[bitMaskWiringSetting.key] != null) {
                if (bitMaskWiringSetting.key === "w2_aux" || bitMaskWiringSetting.key === "y2_gl") {
                    validateArrayValue(bitMaskWiringSetting.key, wiring_settings[bitMaskWiringSetting.key], [0, 1, 10]);
                } else if (bitMaskWiringSetting.key === "ob_mode") {
                    validateArrayValue(bitMaskWiringSetting.key, wiring_settings[bitMaskWiringSetting.key], [0, 1, 11]);
                } else {
                    validateArrayValue(bitMaskWiringSetting.key, wiring_settings[bitMaskWiringSetting.key], [0, 1]);
                }
                wiringSettingValue = wiringSettingValue | wiring_settings[bitMaskWiringSetting.key] << bitMaskWiringSetting.value;
            }
        }
        wiringSets.push(wiringSettingValue);
    }
    return wiringSets;
}

function setOBMode(ob_mode) {
    validateArrayValue("Ob_mode", ob_mode, [0, 1]);
    return [0xFF, 0xB5, ob_mode];
}

function setFreezeProtection(enable, temperature) {
    validateArrayValue("freeze_protection_config.enable", enable, [0, 1]);
    isNumber("Freeze Protection temperature", temperature);
    var temp = valueToInt(temperature, 10);
    ;
    return [0xFF, 0xB0, enable, temp & 0xFF, (temp >>> 8) & 0xFF];
}

function setTemperatureTarget(mode, temperature) {
    validateArrayValue("Temperature Control Mode", mode, [0, 1, 2, 3]);
    isNumber("Temperature target", temperature);
    var temp = valueToInt(temperature, 10);
    return [0xFF, 0xFA, mode, temp & 0xFF, (temp >>> 8) & 0xFF];
}

function setTemperatureControlMode(mode) {
    validateArrayValue("Temperature Control Mode", mode, [0, 1, 2, 3]);
    return [0xFF, 0xFB, mode];
}

function setTemperatureTolerance(temperature_error, auto_control_temperature_error) {
    isNumber("Target temperature tolerance", temperature_error);
    isNumber("Temperature control tolerance", auto_control_temperature_error);
    return [0xFF, 0xB8, valueToInt(temperature_error, 10), valueToInt(auto_control_temperature_error, 10)];
}

function setFanMode(fan_mode) {
    validateArrayValue("Fan Mode", fan_mode, [0, 1, 2]);
    return [0xFF, 0xB6, fan_mode];
}

function setFanModeWithDelay(enable, fan_delay_time) {
    validateArrayValue("Fan Delay enable", enable, [0, 1]);
    validateIsNumberWithRange("Fan  Duration of delay", fan_delay_time, 5, 55);
    return [0xF9, 0x05, enable, fan_delay_time];
}

function setFanModeWithCirculate(fan_circulate) {
    validateIsNumberWithRange("Fan Circulate", fan_circulate, 5, 55);
    return [0xF9, 0x06, fan_circulate];
}

function setFanRegulateHumidity(enable, regulate_interval) {
    validateArrayValue("Fan Regulate Humidity", enable, [0, 1]);
    validateIsNumberWithRange("Fan Regulate Humidity", regulate_interval, 5, 55);
    return [0xF9, 0x07, enable, regulate_interval];
}

function setHumidityRange(min, max) {
    validateIsNumberWithRange("Humidity Range Min", min, 0, 100);
    validateIsNumberWithRange("Humidity Range Max", max, 0, 100);
    return [0xF9, 0x09, min, max];
}

function setTemperatureDehumidify(enable, temperature_tolerance) {
    validateArrayValue("Temp. Control and Dehumidify  enable", enable, [0, 1]);
    isNumber("Dehumidify tolerance value", temperature_tolerance);
    return [0xF9, 0x0A, enable, valueToInt(temperature_tolerance, 10)];
}

function validateDstConfigDateTime(month, week, weekday, dstTime, startEnd) {
    var timeSplit = dstTime.split(":");
    var times = [parseInt(timeSplit[0]), parseInt(timeSplit[1])];
    if (times[0] < 0 || times[0] > 24) {
        raiseError(startEnd + " hours [" + times[0] + "]. Must be between 0 and 24.");
    }
    if (times[1] < 0 || times[1] > 60) {
        raiseError(startEnd + " minutes [" + times[1] + "]. Must be between 0 and 60.");
    }
    if (month < 1 || month > 12) {
        raiseError(startEnd + " month [" + month + "]. Must be between 1 and 12.");
    }
    if (week < 1 || week > 5) {
        raiseError(startEnd + " week [" + week + "]. Must be between 1 and 5.");
    }
    if (weekday < 1 || weekday > 7) {
        raiseError(startEnd + " weekday [" + weekday + "]. Must be between 1 and 7.");
    }
    var dayWeek = (week << 4) | weekday;
    var time = times[0] * 60 + times[1];
    return [month, dayWeek, time & 0xFF, (time >>> 8) & 0xFF];
}

function validateArrayValue(nameValue, value, enable_values) {
    if (enable_values.indexOf(value) === -1) {
        raiseError(nameValue + " [" + value + "]. Must be one of [" + enable_values.join(", "));
    }
}

function validateIsNumberWithRange(nameValueWithRange, valueWithRange, range_min, range_max) {
    isNumber(nameValueWithRange, valueWithRange);
    if (valueWithRange < range_min || valueWithRange > range_max) {
        raiseError(nameValueWithRange + " must be in range [" + range_min + ", " + range_max + "]");
    }
}

function isNumber(nameValue, value) {
    if (value === null || isDecimal(value) === -1) {
        raiseError(nameValue + " must be a number.");
    }
}

function valueToInt(value, factor) {
    return (value * factor).intValue();

}


// Result object with encoded downlink payload
var result = {

    // downlink data content type: JSON, TEXT or BINARY (base64 format)
    contentType: "TEXT",

    // downlink data hexadecimal
    data: base64ToHex(data).toLowerCase(),

    // downlink data (base64 format)
    // data: data,

    // Optional: To validate a value
    // dataBas64: data,
    // dataHex: base64ToHex(data),

    // Optional metadata object presented in key/value format
    metadata: {
        DevEUI: metadata.cs_devEui,
        fPort: metadata.cs_fPort
    }
};

return result;
