// If you want to parse incoming data somehow, you can add your code to this function.
// input: bytes
// expected output:
//  {
//    "attributes": {"attributeKey": "attributeValue"},
//    "telemetry": {"telemetryKey": "telemetryValue"}
//  }
//
// In the example - bytes will be saved as HEX string and also parsed as light level, battery level and PIR sensor value.
//

// "G" Uplink; "G/GH" Downlink === "g"
// "CL&CN" Downlink === "cl_cn"
var bitMaskWiringSettingss =[
    {"y1": 0, "g": 2, "ob": 4, "w1": 6},
    {"e": 0, "cl_cn": 2, "pek": 4, "w2_aux": 6},
    {"y2_gl": 0, "ob_mode": 2}
];

function decodePayload(inputArray) {
    var output = { attributes:{}, telemetry: {} };
    // --- Decoding code --- //
    var input = byteArrayToExecutionArrayList(inputArray);
    output.telemetry.HEX_bytes = bytesToHex(input);

    var decoded = {};
    decoded.hexString = bytesToHex(input);

    for (var i = 0; i < input.length - 2; ) {
        var channel_id = input[i++] & 0xff;
        var channel_type = input[i++] & 0xff;

        //     // IPSO VERSION
        if (channel_id === 0xFF && channel_type === 0x01) {
            decoded.ipso_version = readProtocolVersion(input[i]);
            i += 1;
        }
        // HARDWARE VERSION
        else if (channel_id === 0xFF && channel_type === 0x09) {
            decoded.hardware_version = readHardwareVersion(input.slice(i, i + 2));
            i += 2;
        }
        // FIRMWARE VERSION
        else if (channel_id === 0xFF && channel_type === 0x0A) {
            decoded.firmware_version = readFirmwareVersion(input.slice(i, i + 2));
            i += 2;
        }
        // DEVICE STATUS
        else if (channel_id === 0xFF && channel_type === 0x0B) {
            decoded.device_status = "on";
            i += 1;
        }
        // LORAWAN CLASS TYPE
        else if (channel_id === 0xFF && channel_type === 0x0F) {
            decoded.lorawan_class = readLoRaWANClass(input[i] & 0xff);
            i += 1;
        }
        // PRODUCT SERIAL NUMBER
        else if (channel_id === 0xFF && channel_type === 0x16) {
            decoded.sn = readSerialNumber(input.slice(i, i+ 8));
            i += 8;
        }
        // TSL VERSION
        else if (channel_id === 0xFF && channel_type === 0xFF) {
            decoded.tsl_version = readFirmwareVersion(input.slice(i, i + 2));
            i += 2;
        }
        // TEMPERATURE
        else if (channel_id === 0x03 && channel_type === 0x67) {
            decoded.temperature = parseBytesIntToFloat(input, i, 2, false) / 10;
            i += 2;
        }
        // TEMPERATURE TARGET
        else if (channel_id === 0x04 && channel_type === 0x67) {
            decoded.temperature_target = parseBytesIntToFloat(input, i, 2, false) / 10;
            i += 2;
        }
        // TEMPERATURE CONTROL
        else if (channel_id === 0x05 && channel_type === 0xE7) {
            var temperature_control = input[i];
            decoded.temperature_control_mode = readTemperatureCtlMode(temperature_control & 0x03);
            decoded.temperature_control_status = readTemperatureCtlStatus((temperature_control >>> 4) & 0x0f);
            i += 1;
        }
        // FAN CONTROL
        else if (channel_id === 0x06 && channel_type === 0xE8) {
            var valueFanControl = input[i];
            decoded.fan_mode = readFanMode(valueFanControl & 0x03);
            decoded.fan_status = readFanStatus((valueFanControl >>> 2) & 0x03);
            i += 1;
        }
        // PLAN EVENT
        else if (channel_id === 0x07 && channel_type === 0xBC) {
            decoded.plan_event = readPlanEvent(input[i] & 0x0f);
            i += 1;
        }
        // SYSTEM STATUS
        else if (channel_id === 0x08 && channel_type === 0x8E) {
            decoded.system_status = readSystemStatus(input[i] & 0xff);
            i += 1;
        }
        // HUMIDITY
        else if (channel_id === 0x09 && channel_type === 0x68) {
            decoded.humidity = parseBytesIntToFloat(input, i, 1) / 2;
            i += 1;
        }
        // RELAY STATUS
        else if (channel_id === 0x0A && channel_type === 0x6E) {
            decoded.wires_relay = readWiresRelay(input[i] & 0xff);
            i += 1;
        }
        // PLAN
        else if (channel_id === 0xFF && channel_type === 0xC9) {
            var schedule = {};
            schedule.type = readPlanType(input[i]);
            schedule.index = (input[i + 1] & 0xff) + 1;
            schedule.plan_enable = input[i + 2] === 0 ? "disable" : "enable";
            schedule.week_recycle = readWeekRecycleSettings(input[i + 3] & 0xff);
            var time_mins = parseBytesToInt(input, i + 4, 2, false);
            schedule.time = String.format("%02d:%02d", (int) (time_mins / 60), time_mins % 60);
            i += 6;

            if (decoded.plan_schedule === null) {
                decoded.plan_schedule = [];
            }
            schedule.i = i ;
            decoded.plan_schedule.push(schedule);
        }
        // PLAN SETTINGS
        else if (channel_id === 0xFF && channel_type === 0xC8) {
            var plan_setting = {};
            plan_setting.type = readPlanType(input[i] & 0xff);
            plan_setting.temperature_ctl_mode = readTemperatureCtlMode(input[i + 1] & 0xff);
            plan_setting.fan_mode = readFanMode(input[i + 2] & 0xff);
            plan_setting.temperature_target = input[i + 3] & 0x7f;
            plan_setting.temperature_unit = readTemperatureUnit(input[i + 3] >>> 7);
            plan_setting.temperature_error = parseBytesIntToFloat(input, i + 4, 1, false) / 10;
            i += 5;

            if (decoded.plan_settings === null) {
                decoded.plan_settings = [];
            }
            decoded.plan_settings.push(plan_setting);
        }
        // WIRES
        else if (channel_id === 0xFF && channel_type === 0xCA) {
            decoded.wires = readWires([input[i] & 0xff, input[i + 1] & 0xff, input[i + 2] & 0xff]);
            decoded.ob_mode = readObMode((input[i + 2] >>> 2) & 0x03);
            i += 3;
        }
        // TEMPERATURE MODE SUPPORT
        else if (channel_id === 0xFF && channel_type === 0xCB) {
            decoded.temperature_control_mode_enable = readTemperatureCtlModeEnable(input[i] & 0xff);
            decoded.temperature_control_status_enable = readTemperatureCtlStatusEnable(input[i + 1] & 0xff, input[i + 2] & 0xff);
            i += 3;
        }
        // CONTROL PERMISSIONS
        else if (channel_id === 0xFF && channel_type === 0xF6) {
            decoded.control_permissions = input[i] === 1 ? "remote control" : "thermostat";
            i += 1;
        }
        // TEMPERATURE ALARM
        else if (channel_id === 0x83 && channel_type === 0x67) {
            decoded.temperature = parseBytesToInt(input, i, 2, false) / 10;
            decoded.temperature_alarm = readTemperatureAlarm(input[i + 2] & 0xff);
            i += 3;
        }
        // TEMPERATURE EXCEPTION
        else if (channel_id === 0xB3 && channel_type === 0x67) {
            decoded.temperature_exception = readException(input[i]);
            i += 1;
        }
        // HUMIDITY EXCEPTION
        else if (channel_id === 0xB9 && channel_type === 0x68) {
            decoded.humidity_exception = readException(input[i] & 0xff);
            i += 1;
        }
        // HISTORICAL DATA
        else if (channel_id === 0x20 && channel_type === 0xce) {
            var timestamp = parseBytesToInt(input, i, 4, false);
            var value1 = parseBytesToInt(input, i+4, 2, false);
            var value2 = parseBytesToInt(input, i+6, 2, false);
            var data = {};
            data.timestamp = timestamp;

            // fan_mode
            data.fan_mode = readFanMode(value1 & 0x03);
            data.fan_status = readFanStatus((value1 >>> 2) & 0x03);
            data.system_status = readSystemStatus((value1 >>> 4) & 0x01);

            // temperature_ctl_mode
            var temperature = 1.0 * ((value1 >>> 5) & 0x7ff) / 10 - 100;
            data.temperature = toFixed(temperature, 1);
            data.temperature_ctl_mode = readTemperatureCtlMode(value2 & 0x03);
            data.temperature_ctl_status = readTemperatureCtlStatus((value2 >>> 2) & 0x07);
            var temperature_target =  1.0 * ((value2 >>> 5) & 0x7ff) / 10 - 100;
            data.temperature_target = toFixed(temperature_target, 1);
            i += 8;

            decoded.history = [];
            decoded.history.push(data);
        }
    }
    output.telemetry = decoded;
    return output;
}

function readProtocolVersion(bytes) {
    var minor = (bytes & 0xf0) >> 4;
    var major = bytes & 0x0f;
    return "v" + major + "." + minor;
}

function readHardwareVersion(bytes) {
    var major = bytes[0] & 0xff;
    var minor = (bytes[1] & 0xff) >> 4;
    return "v" + major + "." + minor;
}

function readFirmwareVersion(bytes) {
    var major = bytes[0] & 0xff;
    var minor = bytes[1] & 0xff;
    return "v" + major + "." + minor;
}

function readSerialNumber(bytes) {
    return bytesToHex(bytes);
}

function readLoRaWANClass(type) {
    switch (type) {
        case 0x00:
            return "ClassA";
        case 0x01:
            return "ClassB";
        case 0x02:
            return "ClassC";
        case 0x03:
            return "ClassCtoB";
        default:
            return "unknown";
    }
}

function readTemperatureUnit(type) {
    switch (type) {
        case 0x00:
            return "℃";
        case 0x01:
            return "℉";
        default:
            return "unknown";
    }
}

function readTemperatureAlarm(type) {
    switch (type) {
        case 0x01:
            return "emergency heating timeout alarm";
        case 0x02:
            return "auxiliary heating timeout alarm";
        case 0x03:
            return "persistent low temperature alarm";
        case 0x04:
            return "persistent low temperature alarm release";
        case 0x05:
            return "persistent high temperature alarm";
        case 0x06:
            return "persistent high temperature alarm release";
        case 0x07:
            return "freeze protection alarm";
        case 0x08:
            return "freeze protection alarm release";
        case 0x09:
            return "temperature threshold alarm";
        case 0x0a:
            return "temperature threshold alarm release";
        default:
    }
}

function readException(type) {
    switch (type) {
        case 0x01:
            return "collection failure";
        case 0x02:
            return "out of measuring range";
        default:
    }
}

function readPlanEvent(type) {
    switch (type) {
        case 0x00:
            return "not executed";
        case 0x01:
            return "wake";
        case 0x02:
            return "away";
        case 0x03:
            return "home";
        case 0x04:
            return "sleep";
        default:
    }
}

function readPlanType(type) {
    switch (type) {
        case 0x00:
            return "wake";
        case 0x01:
            return "away";
        case 0x02:
            return "home";
        case 0x03:
            return "sleep";
        default:
    }
}

function readFanMode(type) {
    switch (type) {
        case 0x00:
            return "auto";
        case 0x01:
            return "on";
        case 0x02:
            return "circulate";
        default:
    }
}

function readFanStatus(type) {
    switch (type) {
        case 0x00:
            return "off";
        case 0x01:
            return "high speed";
        case 0x02:
            return "low speed";
        case 0x03:
            return "on";
        default:
    }
}

function readSystemStatus(type) {
    switch (type) {
        case 0x00:
            return "off";
        case 0x01:
            return "on";
        default:
    }
}

function readTemperatureCtlMode(type) {
    switch (type) {
        case 0x00:
            return "heat";
        case 0x01:
            return "em heat";
        case 0x02:
            return "cool";
        case 0x03:
            return "auto";
        default:
    }
}

function readTemperatureCtlStatus(type) {
    switch (type) {
        case 0x00:
            return "standby";
        case 0x01:
            return "stage-1 heat";
        case 0x02:
            return "stage-2 heat";
        case 0x03:
            return "stage-3 heat";
        case 0x04:
            return "stage-4 heat";
        case 0x05:
            return "em heat";
        case 0x06:
            return "stage-1 cool";
        case 0x07:
            return "stage-2 cool";
        default:
    }
}

function readWires(wireArrays) {
    var wire = [];
    for (var i = 0; i < 3; i++) {
        var wireArray = wireArrays[i];
        var bitMaskWiringSettings = bitMaskWiringSettingss[i];
        foreach(bitMaskWiringSetting : bitMaskWiringSettings.entrySet()) {
            var wireArrayValue = ((wireArray >>> bitMaskWiringSetting.value) & 0x03);
            if (bitMaskWiringSetting.key != "ob_mode" && wireArrayValue != 0) {
                if (i === 1 && bitMaskWiringSetting.key === "w2_aux") {
                    switch (wireArrayValue) {
                        case 1:
                            wire.push("w2");
                            break;
                        case 2:
                            wire.push("aux");
                            break;
                        default:
                    }
                } else if (i === 2 && bitMaskWiringSetting.key === "y2_gl") {
                    switch (wireArrayValue) {
                        case 1:
                            wire.push("y2");
                            break;
                        case 2:
                            wire.push("gl");
                            break;
                        default:
                    }
                } else {
                    wire.push(bitMaskWiringSetting.key);
                }
            }
        }
    }
    return wire;
}

function readWiresRelay(status) {
    var relay = {};
    var statusBinaryArray =  parseByteToBinaryArray(status, 7, false);
    relay.y1 = statusBinaryArray[0];
    relay.y2_gl = statusBinaryArray[1];
    relay.w1 = statusBinaryArray[2];
    relay.w2_aux = statusBinaryArray[3];
    relay.e = statusBinaryArray[4];
    relay.g = statusBinaryArray[5];
    relay.ob = statusBinaryArray[6];
    return relay;
}

function readObMode(type) {
    switch (type) {
        case 0x00:      // 00
            return "cool";
        case 0x01:      // 01
            return "heat";
        case 0x03:      // 11
            return "Keep Original Setting";
        default:
    }
}

function readTemperatureCtlModeEnable(type) {
    var enable = [];
    if (((type >>> 0) & 0x01) !=0) {
        enable.push("heat");
    }
    if (((type >>> 1) & 0x01) !=0) {
        enable.push("em heat");
    }
    if (((type >>> 2) & 0x01) !=0) {
        enable.push("cool");
    }
    if (((type >>> 3) & 0x01) !=0) {
        enable.push("auto");
    }
    return enable;
}

function readTemperatureCtlStatusEnable(heat_mode, cool_mode) {
    var enable = [];
    if (((heat_mode >>> 0) & 0x01) !=0) {
        enable.push("stage-1 heat");
    }
    if (((heat_mode >>> 1) & 0x01) !=0) {
        enable.push("stage-2 heat");
    }
    if (((heat_mode >>> 2) & 0x01) !=0) {
        enable.push("stage-3 heat");
    }
    if (((heat_mode >>> 3) & 0x01) !=0) {
        enable.push("stage-4 heat");
    }
    if (((heat_mode >>> 4) & 0x01) !=0) {
        enable.push("aux heat");
    }

    // bit0: stage-1 cool, bit1: stage-2 cool
    if (((cool_mode >>> 0) & 0x03) !=0) {
        enable.push("stage-1 cool");
    }
    if (((cool_mode >>> 1) & 0x03) !=0) {
        enable.push("stage-2 cool");
    }
    return enable;
}

function readWeekRecycleSettings(type) {
    // bit1: "mon", bit2: "tues", bit3: "wed", bit4: "thur", bit5: "fri", bit6: "sat", bit7: "sun"
    var week_enable = [];
    if (((type >>> 1) & 0x01) != 0){
        week_enable.push("Mon.");
    }
    if (((type >>> 2) & 0x01) != 0){
        week_enable.push("Tues.");
    }
    if (((type >>> 3) & 0x01) != 0){
        week_enable.push("Wed.");
    }
    if (((type >>> 4) & 0x01) != 0){
        week_enable.push("Thur.");
    }
    if (((type >>> 5) & 0x01) != 0){
        week_enable.push("Fri.");
    }
    if (((type >>> 6) & 0x01) != 0){
        week_enable.push("Sat.");
    }
    if (((type >>> 7) & 0x01) != 0){
        week_enable.push("Sun.");
    }
    return week_enable;
}

// --- attributes and telemetry objects ---
var telemetry = {};
var attributes = {};
// --- attributes and telemetry objects ---

// --- Timestamp parsing
var dateString = data.time;
var timestamp = -1;
if (dateString != null) {
    timestamp = new Date(dateString).getTime();
    if (timestamp == -1) {
        var secondsSeparatorIndex = dateString.lastIndexOf('.') + 1;
        var millisecondsEndIndex = dateString.lastIndexOf('+');
        if (millisecondsEndIndex == -1) {
            millisecondsEndIndex = dateString.lastIndexOf('Z');
        }
        if (millisecondsEndIndex == -1) {
            millisecondsEndIndex = dateString.lastIndexOf('-');
        }
        if (millisecondsEndIndex == -1) {
            if (dateString.length >= secondsSeparatorIndex + 3) {
                dateString = dateString.substring(0, secondsSeparatorIndex + 3);
            }
        } else {
            dateString = dateString.substring(0, secondsSeparatorIndex + 3) +
                dateString.substring(millisecondsEndIndex, dateString.length);
        }
        timestamp = new Date(dateString).getTime();
    }
}
// If we cannot parse timestamp - we will use the current timestamp
if (timestamp == -1) {
    timestamp = Date.now();
}
// --- Timestamp parsing

// You can add some keys manually to attributes or telemetry
attributes.deduplicationId = data.deduplicationId;

// You can exclude some keys from the result
var excludeFromAttributesList = ["deviceName", "rxInfo", "confirmed", "data", "deduplicationId","time", "adr", "dr", "fCnt"];
var excludeFromTelemetryList = ["data", "deviceInfo", "txInfo", "devAddr", "adr", "time", "fPort", "region_common_name", "region_config_id", "deduplicationId"];

// Message parsing
// To avoid paths in the decoded objects we passing false value to function as "pathInKey" argument.
// Warning: pathInKey can cause already found fields to be overwritten with the last value found.

var telemetryData = toFlatMap(data, excludeFromTelemetryList, false);
var attributesData = toFlatMap(data, excludeFromAttributesList, false);

var uplinkDataList = [];

// Passing incoming bytes to decodePayload function, to get custom decoding
var customDecoding = decodePayload(base64ToBytes(data.data));

// Collecting data to result
if (customDecoding.?telemetry.size() > 0) {
    telemetry.putAll(customDecoding.telemetry);
}

if (customDecoding.?attributes.size() > 0) {
    attributes.putAll(customDecoding.attributes);
}

telemetry.putAll(telemetryData);
attributes.putAll(attributesData);

var result = {
    deviceName: deviceName,
    deviceType: deviceType,
    groupName: groupName,
    attributes: attributes,
    telemetry: {
        ts: timestamp,
        values: telemetry
    }
};

return result;
