var data = decodeToJson(payload);

var deviceName = data.end_device_ids.device_id;
var deviceType = data.end_device_ids.application_ids.application_id;
var groupName = 'IAQ devices';
// var customerName = 'Customer A';
// use assetName and assetType instead of deviceName and deviceType
// to automatically create assets instead of devices.
// var assetName = 'Asset A';
// var assetType = 'building';

// If you want to parse incoming data somehow, you can add your code to this function.
// input: bytes
// expected output:
//  {
//    "attributes": {"attributeKey": "attributeValue"},
//    "telemetry": {"telemetryKey": "telemetryValue"}
//  }
//
// In the example - bytes will be saved as HEX string and also parsed as light level, battery level and PIR sensor value.
//

function decodeFrmPayload(input) {
  var output = { attributes:{}, telemetry: {} };
  // --- Decoding code --- //
  //  output.telemetry.f12 = 4;
  var fPort = input.get("f_port");

  if (fPort != null && fPort === 6) {
      var frmPayloadBytes = base64ToBytes(input.get("frm_payload"));
      reportDataCmd(byteArrayToExecutionArrayList(frmPayloadBytes), output);
  } else {
      raiseError("Data decoding error. The 'f_port' = " + fPort + ", f_port must be: 6");
  }
  return output;
}


function reportDataCmd(frmPayload, output) {
  var i = 0;
  while (i < (frmPayload.length - 3)) {
    var version = frmPayload[i++] & 0xff;
    var deviceTypeId = frmPayload[i++] & 0xff;
    var reportType = frmPayload[i++] & 0xff;
    if (i < (frmPayload.length - 7) && version === 0x01 && deviceTypeId === 0x4A) {
      switch (reportType) {
        case 0:
          reportDataCmdPort6Type0(frmPayload.slice(i, i + 8), output);
          i +=8;
          break;
        case 1:
          var len = 8;
          if (i < (frmPayload.length - 18) && (frmPayload[i + 8] & 0xff) ===  0x01
              && (frmPayload[i + 9] & 0xff) === 0x4A && (frmPayload[i + 10] & 0xff) === 2) {
            len = 19;
          }
          reportDataCmdPort6Type1_2(frmPayload.slice(i, i + len), output);
          i +=len;
          break;
        case 3:
          reportDataCmdPort6Type3(frmPayload.slice(i, i + 8), output);
          i +=8;
          break;
        case 4:
          reportDataCmdPort6Type4(frmPayload.slice(i, i + 8), output);
          i +=8;
          break;
        default:

      }
    }
  }
  if (output.?telemetry.size() === 0 && output.?attributes.size() === 0) {
    output.telemetry.frm_payload_result = "isEmpty";
  }
}

function reportDataCmdPort6Type0(data, output) {
  var softwareVersion = parseBytesIntToFloat(data, 0, 1)/10;
  var hardwareVersion = data[1];
  var dateCodeStr = bytesToHex(data.slice(2, 4));
  output.attributes.SWver = softwareVersion;
  output.attributes.SWverSt = "V" + softwareVersion;
  output.attributes.HWver = hardwareVersion;
  output.attributes.Datecode = dateCodeStr;

}

function reportDataCmdPort6Type1_2(data, output) {
  var current = reportDataCmdPort6CurrentVolt(data, output);
  var multiplier1;
  if (data.length === 8) {
    multiplier1 = parseBytesToInt(data.slice(7));
    output.telemetry.Current1 = current.current1 * multiplier1;
    output.telemetry.Current2 = current.current2 * multiplier1;
    output.telemetry.Current3 = current.current3 * multiplier1;
    // output.telemetry.multiplier1 = multiplier1;
  } else {
    multiplier1 = parseBytesToInt(data.slice(7, 8));
    var multiplier2 = parseBytesToInt(data.slice(12, 13));
    var multiplier3 = parseBytesToInt(data.slice(13, 14));
    output.telemetry.Current1 = current.current1 * multiplier1;
    output.telemetry.Current2 = current.current2 * multiplier2;
    output.telemetry.Current3 = current.current3 * multiplier3;
    // output.telemetry.multiplier1 = multiplier1;
    // output.telemetry.multiplier2 = multiplier2;
    // output.telemetry.multiplier3 = multiplier3;
  }
}

function reportDataCmdPort6Type3(data, output) {
  var current = reportDataCmdPort6CurrentVolt(data, output);
  var multiplier = multiplierByBit(data[7]);
  multiplier.Current1 = current.current1;
  multiplier.Current2 = current.current2;
  multiplier.Current3 = current.current3;
  // output.telemetry.multiplier = multiplier;
  output.telemetry.Current1 = current.current1 * multiplier.multiplier1;
  output.telemetry.Current2 = current.current2 * multiplier.multiplier2;
  output.telemetry.Current3 = current.current3 * multiplier.multiplier3;
  // output.telemetry.multiplier1 = multiplier.multiplier1;
  // output.telemetry.multiplier2 = multiplier.multiplier2;
  // output.telemetry.multiplier3 = multiplier.multiplier3;
}

function reportDataCmdPort6Type4(data, output) {
  reportDataCmdPort6Volt(data[0], output);
  var currentAlarm = parseByteToBinaryArray(data[1], 6, false);
  // output.telemetry.dataAlarm = data[1];
  // output.telemetry.Alarm = currentAlarm;
  output.telemetry.LowCurrent1Alarm = currentAlarm[0];
  output.telemetry.HighCurrent1Alarm = currentAlarm[1];
  output.telemetry.LowCurrent2Alarm = currentAlarm[2];
  output.telemetry.HighCurren2Alarm = currentAlarm[3];
  output.telemetry.LowCurrent3Alarm = currentAlarm[4];
  output.telemetry.HighCurrent3Alarm = currentAlarm[5];
}

function reportDataCmdPort6CurrentVolt(data, output) {
  reportDataCmdPort6Volt(data[0], output);
  var current = {};
  current.current1 = parseBytesToInt(data.slice(1, 3));
  current.current2 = parseBytesToInt(data.slice(3, 5));
  current.current3 = parseBytesToInt(data.slice(5, 7));
  return current;
}

function reportDataCmdPort6Volt(data, output) {
  var volt = parseByteToBinaryArray(data);
  var voltValue =  parseBinaryArrayToInt(volt, 1) * 0.1;
  output.telemetry.Volt = voltValue;
  if (volt[7] === 1) {
    output.telemetry.LowVolt = true;
  }
}

function multiplierByBit(data) {
  var multiplier = {};
  var binaryArray = parseByteToBinaryArray(data);
  multiplier.multiplier1 = multiplierByBitValue(parseBinaryArrayToInt(binaryArray, 6, 2));
  multiplier.multiplier2 = multiplierByBitValue(parseBinaryArrayToInt(binaryArray, 4, 2));
  multiplier.multiplier3 = multiplierByBitValue(parseBinaryArrayToInt(binaryArray, 2, 2));
  return multiplier;
}

function multiplierByBitValue(index) {
  var results = [1, 5, 10, 100];
  return results[index];
}


// --- attributes and telemetry objects ---
var telemetry = {};
var attributes = {};
// --- attributes and telemetry objects ---

// --- Timestamp parsing
var dateString = data.uplink_message.received_at;
// If data is simulated or device doesn't send his own date string - we will use date from upcoming message, set by network server
if ((data.simulated != null && data.simulated) || dateString == null) {
  dateString = data.received_at;
}
var timestamp = -1;
if (dateString != null) {
  timestamp = new Date(dateString).getTime();
  if (timestamp == -1) {
    var secondsSeparatorIndex = dateString.lastIndexOf('.') + 1;
    var millisecondsEndIndex = dateString.lastIndexOf('+');
    if (millisecondsEndIndex == -1) {
      millisecondsEndIndex = dateString.lastIndexOf('Z');
    }
    if (millisecondsEndIndex == -1) {
      millisecondsEndIndex = dateString.lastIndexOf('-');
    }
    if (millisecondsEndIndex == -1) {
      if (dateString.length >= secondsSeparatorIndex + 3) {
        dateString = dateString.substring(0, secondsSeparatorIndex + 3);
      }
    } else {
      dateString = dateString.substring(0, secondsSeparatorIndex + 3) +
          dateString.substring(millisecondsEndIndex, dateString.length);
    }
    timestamp = new Date(dateString).getTime();
  }
}
// If we cannot parse timestamp - we will use the current timestamp
if (timestamp == -1) {
  timestamp = Date.now();
}
// --- Timestamp parsing

// You can add some keys manually to attributes or telemetry
attributes.devEui = data.end_device_ids.dev_eui;
attributes.fPort = data.uplink_message.f_port;
// We want to save correlation ids as single object, so we are excluding them from attributes parse and add manually
attributes.correlation_ids = data.correlation_ids;

// You can exclude some keys from the result
var excludeFromTelemetryList = ["uplink_token", "gateway_id", "settings", "f_port", "time", "timestamp", "received_at", "network_ids"];
var excludeFromAttributesList = ["uplink_token", "gateway_id", "f_port", "time", "timestamp", "received_at", "session_key_id", "dev_eui"];

// Message parsing
// To avoid paths in the decoded objects we passing false value to function as "pathInKey" argument.
// Warning: pathInKey can cause already found fields to be overwritten with the last value found, e.g. receive_at from uplink_message will be written receive_at in the root.
var telemetryData = toFlatMap(data.uplink_message, excludeFromTelemetryList, false);
var attributesData = {};
attributesData.putAll(toFlatMap(data.uplink_message.settings, excludeFromAttributesList, false));
attributesData.putAll(toFlatMap(data.uplink_message.network_ids, excludeFromAttributesList, false));
attributesData.putAll(toFlatMap(data.end_device_ids, excludeFromAttributesList, false));

// Passing incoming bytes to decodeFrmPayload function, to get custom decoding
var customDecoding = {};
customDecoding = decodeFrmPayload(data.uplink_message);

// Collecting data to result
if (customDecoding.?telemetry.size() > 0) {
  telemetry.putAll(customDecoding.telemetry);
}

if (customDecoding.?attributes.size() > 0) {
  attributes.putAll(customDecoding.attributes);
}

telemetry.putAll(telemetryData);
attributes.putAll(attributesData);

var result = {
  deviceName: deviceName,
  deviceType: deviceType,
//  assetName: assetName,
//  assetType: assetType,
//  customerName: customerName,
  groupName: groupName,
  attributes: attributes,
  telemetry: {
    ts: timestamp,
    values: telemetry
  }
};

return result;
