function parseDateToTimestamp(dateString) {
    var date = new Date(dateString);
    var timestamp = date.getTime();

    // If we cannot parse timestamp - we will use the current timestamp
    if (timestamp == -1) {
        timestamp = Date.now();
    }

    return timestamp;
}

function getGatewayInfo() {
    var gatewayList = data.uplink_message.?rx_metadata;
    var maxRssi = Integer. MIN_VALUE;
    var gatewayInfo = {};

    foreach (gateway : gatewayList) {
        if(gateway.rssi > maxRssi) {
            maxRssi = gateway.rssi;
            gatewayInfo = gateway;
        }
    }

    return gatewayInfo;
}

function processTelemetryData(telemetry, addDataToTelemetry) {
    if (telemetry.size >= 1) {
        telemetry = addDataToTelemetries(telemetry, addDataToTelemetry);
    }
    else {
        telemetry.add(addDataToTelemetry);
    }

    return telemetry;
}

function addDataToTelemetries(telemetries, addDataToTelemetry) {
    foreach(telemetry : telemetries) {
        foreach(element : addDataToTelemetry.entrySet()) {
            if(!telemetry["values"].keys.contains(element.key)) {
                telemetry["values"][element.key] = element.value;
            }
        } 
    }

    return telemetries;
}

function getWatermarkData(watermarkFrequency) {
    var decodedWatermark = {};

    var watermarkTension = 0;
    if (watermarkFrequency > 6430){
		watermarkTension = 0;
	} else if (watermarkFrequency >= 4330 && watermarkFrequency <= 6430){
	    watermarkTension = 9.000 - (watermarkFrequency - 4330.000) * 0.004286;
	} else if (watermarkFrequency >= 2820 && watermarkFrequency < 4330){
		watermarkTension = 15.000 - (watermarkFrequency - 2820.000) * 0.003974;
	} else if (watermarkFrequency >= 1110 && watermarkFrequency < 2820){
		watermarkTension = 35.000 - (watermarkFrequency - 1110.000) * 0.01170;
	} else if (watermarkFrequency >= 770 && watermarkFrequency < 1110){
		watermarkTension = 55.000 - (watermarkFrequency - 770.000) * 0.05884;
	} else if (watermarkFrequency >= 600 && watermarkFrequency < 770){
		watermarkTension = 75.000 - (watermarkFrequency - 600.000) * 0.1176;
	} else if (watermarkFrequency >= 485 && watermarkFrequency < 600){
		watermarkTension = 100.000 - (watermarkFrequency - 485.000) * 0.2174;
    } else if (watermarkFrequency >= 293 && watermarkFrequency < 485){
		watermarkTension = 200.000 - (watermarkFrequency - 293.000) * 0.5208;
	} else {
	    watermarkTension = 200;
	}					
	decodedWatermark.watermarkTension = roundUsingMathRound(watermarkTension, 0);
	decodedWatermark.watermarkFrequency = watermarkFrequency;

    return decodedWatermark;
}

function roundUsingMathRound(value, places) {
    if (places >=  0) {
        var factor = Math.pow(10, places);
        return Math.round(value * factor) / factor;   
    }

    return value;
}

function getAlarmStatus(bit) {
    var alarmResult = "Invalid";

    if (bit === 0) {
        alarmResult = "No Alarm";
    } else if (bit === 1 || bit === 255) {
        alarmResult = "Alarm";
    } else {
        alarmResult = "Invalid";
    }

    return alarmResult;
}

