					case 4:
						output.attributes.region = "IN865";
						break;
					case 6:
						output.attributes.region = "KR920";
						break;
					case 7:
						output.attributes.region = "RU864";
						break;
					default:
						output.attributes.region = "Invalid";
				}
				i += 7;
            }
        }
    }
    else if (fPort == 101) {
        var size = input.length;
        var responses = [];

        var index = 0;
        while (index < size) {
            var downlinkFcnt = input[index++] & 0xFF; 
            var numInvalidWrites = input[index++] & 0xFF; 

            if (numInvalidWrites > 0) {
                var invalidRegisters = [];
                for (var i = 0; i < numInvalidWrites; i++) {
                    invalidRegisters.add(String.format("0x%02X", input[index + i]));
                }
                index += numInvalidWrites;

                responses.add(String.format(
                    "%d Invalid write command(s) from DL:%d for register(s): %s",
                    numInvalidWrites, downlinkFcnt, String.join(", ", invalidRegisters)
                ));
            } else {
                responses.add(String.format("All write commands from DL:%d were successful", downlinkFcnt));
            }
        }

        decoded.response = responses;
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    // --- Decoding code --- //
    return output;
}


