// Decode an uplink message from a buffer
// payload - array of bytes
// metadata - key/value object

/** Decoder **/

// decode payload to string
var payloadStr = decodeToString(payload);

// decode payload to JSON
// var data = decodeToJson(payload);

var deviceName = 'Device A';
var deviceType = 'thermostat';
var customerName = 'Customer C';
var groupName = 'thermostat devices';
var manufacturer = 'Example corporation';
// use assetName and assetType instead of deviceName and deviceType
// to automatically create assets instead of devices.
// var assetName = 'Asset A';
// var assetType = 'building';

// Result object with device/asset attributes/telemetry data
var result = {
// Use deviceName and deviceType or assetName and assetType, but not both.
   deviceName: deviceName,
   deviceType: deviceType,
// assetName: assetName,
// assetType: assetType,
// customerName: customerName,
   groupName: groupName,
   attributes: {
       model: 'Model A',
       serialNumber: 'SN111',
       integrationName: metadata['integrationName'],
       manufacturer: manufacturer
   },
   telemetry: {
       temperature: 42,
       humidity: 80,
       rawData: payloadStr
   }
};

/** Helper functions **/

function decodeToString(payload) {
   return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
   // covert payload to string.
   var str = decodeToString(payload);

   // parse string to JSON
   var data = JSON.parse(str);
   return data;
}

return result;

configuration.tbelDecoder:
var data = decodeToJson(payload);

var deviceName = data.EUI;
var deviceType = 'Electricity Meter';
var groupName = 'Electricity Meters';
// var customerName = 'Customer A';
// use assetName and assetType instead of deviceName and deviceType
// to automatically create assets instead of devices.
// var assetName = 'Asset A';
// var assetType = 'building';


// --- attributes and telemetry objects ---
var telemetryArray = [];
var attributes = {};
// --- attributes and telemetry objects ---

var timestamp = -1;
if(data.ts != null)
{
    timestamp = data.ts;
}
if (timestamp == -1) {
  timestamp = Date.now();
}

attributes.putAll( getVersionControl() );
attributes.devEui = data.EUI;

if(data.port == null)
{
    // No uplink : end of decoder
    var result = {
        deviceName: deviceName,
        deviceType: deviceType,
        groupName: groupName,
        attributes: attributes,
        telemetry: telemetryArray
    }; 
    return result;
}

var telemetryData = {};
telemetryData.fPort = data.port;
telemetryData.confirmed = data.confirmed;
telemetryData.fCnt = data.fcnt;
telemetryArray.push({ts:timestamp, values:telemetryData});

// input uplink
var input = {
    fPort: data.port,
    bytes: hexStringToIntList(data.data),
    variables: null,
};

// Decode uplink
var up = {
    timestamp : timestamp,
    fPort: input.fPort,
    object: decodeUplink(input).data,
    isDownlinkAnswer: false,
    isBasicInfo: false,
};

if(up.fPort == 50)
{
    up.isDownlinkAnswer = true;
    /* hardwareVersion indicates that is basic info so save them to attributes */
    if(up.object.hardwareVersion != null)
    {
        up.isBasicInfo = true;
    }
} else if(up.fPort >= 1 && up.fPort <= 10)
{
    if(up.object.warning != null || up.object.info != null)
    {
        up.isDownlinkAnswer = true;
    }
}

attributes.putAll( getAttributes(up) );
var telemetry = getTelemetry(up);
if(telemetry != null){
    telemetryArray.push(telemetry);
}

var result = {
    deviceName: deviceName,
    deviceType: deviceType,
    // assetName: assetName,
    // assetType: assetType,
    groupName: groupName,
    attributes: attributes,
    telemetry: telemetryArray
};

return result;



/**
 * @brief converts device parameters into attributes
 * 
 * @param up uplink object 
 * 
 * @return device attributes
 */
function getAttributes(up)
{
	var params = {};
    params.latestFPort = up.fPort;

	if(up.isDownlinkAnswer == false)
	{
		return params;
	}

	if(up.object.warning != null )
	{
		params.latestDownlinkStatus = up.timestamp + "-FAILURE";
		return params;
	}

	if(up.object.info != null)
	{
		params.latestDownlinkStatus = up.timestamp + "-SUCCESS";
		return params;
	}
    
    var additionalAttributes = {};
    foreach(element: up.object.entrySet())
    {
        if(up.isBasicInfo){
            if(element.value instanceof java.util.Map && element.value.data != null){
                additionalAttributes[element.key] = element.value.data;
            }else{
                additionalAttributes[element.key] = element.value;
            }
        }else{
            var capitalizeKey = element.key.substring(0, 1).toUpperCase() + element.key.substring(1);
            if(element.value instanceof java.util.Map && element.value.data != null){
                additionalAttributes["latestSetting" + capitalizeKey] = element.value.data;
            }else{
                additionalAttributes["latestSetting" + capitalizeKey] = element.value;
            }
        }
    }

    if(up.isBasicInfo)
    {
        params.putAll(additionalAttributes);
    }else {
        params.latestDownlinkStatus = up.timestamp + "-SUCCESS";
        params.latestIncomingSettings = additionalAttributes;
    }

	return params;
}

/**
 * @brief converts device measurements into telemetry
 * 
 * @param up uplink object 
 * 
 * @return device telemetry
 */
function getTelemetry(up)
{
	if(up.isBasicInfo || up.isDownlinkAnswer)
	{
		return null;
	}

    var ts = up.timestamp;
    if(up.object.dataloggerTimestamp != null)
    {
        // use dataloggerTimestamp when it exists
        ts = up.object.dataloggerTimestamp * 1000;   // dataloggerTimestamp is in seconds
        up.object.remove("dataloggerTimestamp");
    }

    var values = {};
    foreach(element: up.object.entrySet())
    {
        if(element.value instanceof java.util.Map && element.value.data != null)
        {
            values[element.key] = element.value.data;
        }else{
            values[element.key] = element.value;
        }
    }
    
    return {ts: ts, values: values};
}


