function getSizeBasedOnChannel(bytes, index, channel)
{
    var size = 0;
    while(index + size < bytes.length && bytes[index + size] != channel)
    {
        size = size + 1;
    }
    return size;
}

function getStringFromBytesBigEndianFormat(bytes, index, size)
{
    var byteArray = [];
    for(var i=0; i<size; i=i+1)
    {
        byteArray.push(bytes[index+i]);
    }
    return bytesToString(byteArray);
}

function getStringFromBytesLittleEndianFormat(bytes, index, size)
{
    var byteArray = [];
    for(var i=(size - 1); i>=0; i=i-1)
    {
        byteArray.push(bytes[index+i]);
    }
    return bytesToString(byteArray);
}

function getValueFromBytesBigEndianFormat(bytes, index, size)
{
    if(size >= 4){
        return parseBytesToLong(bytes, index, size, true);
    }
    return parseBytesToInt(bytes, index, size, true);
}

function getValueFromBytesLittleEndianFormat(bytes, index, size)
{
    if(size >= 4){
        return parseBytesToLong(bytes, index, size, false);
    }
    return parseBytesToInt(bytes, index, size, false);
}

function getDigitStringArrayNoFormat(bytes, index, size)
{
    var hexString = [];
    for(var i=0; i<size; i=i+1)
    {
        hexString.push(Integer.toString(bytes[index+i], 16));
    }
    return hexString;
}

function getDigitStringArrayEvenFormat(bytes, index, size)
{
    var hexString = [];
    for(var i=0; i<size; i=i+1)
    {
        hexString.push(byteToEvenHex(bytes[index+i]));
    }
    return hexString;
}

function toEvenHEX(hex)
{
    if(hex.length == 1)
    {
        return "0"+hex;
    }
    return hex;
}


function byteToEvenHex(val)
{
    return toEvenHEX(Integer.toString(val, 16));
}

function hexStringToIntList(hexString)
{
    var bytes = [];
    var hexByte = "";
    for(var i=0; i<hexString.length; i=i+2)
    {
        hexByte = hexString[i] + hexString[i+1];
        bytes.push(parseHexToInt(hexByte));
    }
    return bytes;
}

/**
 * @brief This function supports conversion to positive or negative value of type INT8, INT16, INT24, INT32, etc.. 
 */
function parseBytesToAnyInt(bytes, offset, length, bigEndian)
{
    var byteArray = [];
    if(length > 4)
    {
        return parseBytesToLong(bytes, offset, length, bigEndian);
    }
    var value = parseBytesToInt(bytes, offset, length, bigEndian);
    var isValueNegative = false;
    var CONFIG_TYPE = {
        INT8_MAX : 127,
        INT16_MAX : 32767,
        INT24_MAX : 8388607,
    };
    
    if(length == 1){
        if(value > CONFIG_TYPE.INT8_MAX){
            isValueNegative = true;
        }
    }else if(length == 2){
        if(value > CONFIG_TYPE.INT16_MAX){
            isValueNegative = true;
        }
    }else if(length == 3){
        if(value > CONFIG_TYPE.INT24_MAX){
            isValueNegative = true;
        }
    }

    if(isValueNegative == false)
    {
        return value;
    }

    for(var i=0; i<4; i=i+1){
        if(i < (4 - length)){
            byteArray.push(255);
        }else{
            var index = i - (4 - length);
            if(bigEndian){
                byteArray.push(bytes[offset + index]);
            }else{
                byteArray.push(bytes[offset + (length - 1) - index]);
            }
        }
    }
    return parseBytesToInt(byteArray, 0, 4, bigEndian);
}

