configuration.tbelEncoder:
// Process data from incoming message and metadata
var input = {
    fPort : parseInt(metadata.rc_fPort), // from rule chain
    confirmed : metadata.rc_confirmed === "true" ? true : false, // from rule chain
    data : msg,
    variables: null,
    devEui : metadata.cs_devEui, // from client scope
    devId  : metadata.cs_devId, // from client scope
    applicationId: metadata.cs_applicationId, // from client scope
};

// Result object with encoded downlink payload
var result = {

    // downlink data content type: JSON, TEXT or BINARY (base64 format)
    contentType: "JSON",

    // downlink data
    data: JSON.stringify({
        downlinks:[{
            f_port: input.fPort,
            confirmed: input.confirmed,
            frm_payload: bytesToBase64(encodeDownlink(input).bytes),
            priority: "NORMAL"
        }]
    }),

    // Optional metadata object presented in key/value format
    metadata: {
        eui : input.devId,
        devId : input.devId
    }

};


return result;



