// Encode downlink function. (ChirpStack v4 , TTN)
//
// Input is an object with the following fields:
// - data = Object representing the payload that must be encoded.
// - variables = Object containing the configured device variables.
//
// Output must be an object with the following fields:
// - bytes = Byte array containing the downlink payload.
function encodeDownlink(input) {
    return {
        bytes: Encode(null, input.data, input.variables)
    };
}


/************************************************************************************************************/

// Encode encodes the given object into an array of bytes. (ChirpStack v3)
//  - fPort contains the LoRaWAN fPort number
//  - obj is an object, e.g. {"temperature": 22.5}
//  - variables contains the device variables e.g. {"calibration": "3.5"} (both the key / value are of type string)
// The function must return an array of bytes, e.g. [225, 230, 255, 0]
function Encode(fPort, obj, variables) {
    // Constants for downlink type (Config or Measure)
    var CONFIG_DOWNLINK = {
        TYPE    : "Type",
        CONFIG  : "Config",
        MEASURE : "Measure",
    };

    if(obj[CONFIG_DOWNLINK.TYPE] == CONFIG_DOWNLINK.CONFIG)
    {
        return encodeDeviceConfiguration(obj[CONFIG_DOWNLINK.CONFIG]);
    }
    else if(obj[CONFIG_DOWNLINK.TYPE] == CONFIG_DOWNLINK.MEASURE)
    {
        return encodePeriodicPackage(obj[CONFIG_DOWNLINK.MEASURE]);
    }
    
    return [];
}

// Encode downlink function. (ChirpStack v4 , TTN)
//
// Input is an object with the following fields:
// - data = Object representing the payload that must be encoded.
// - variables = Object containing the configured device variables.
//
// Output must be an object with the following fields:
// - bytes = Byte array containing the downlink payload.
function encodeDownlink(input) {
    return {
        bytes: Encode(null, input.data, input.variables)
    };
}

/************************************************************************************************************/


function encodeDeviceConfiguration(configObj)
{
    // Constants for device configuration 
    var CONFIG_DEVICE = {
        FPORT : 50,
        CHANNEL : 0xFF,
        TYPES : {
            "restart" : {TYPE : 0x0B, SIZE : 1, MIN : 1, MAX : 1,},
            "primaryCurrentTransformerRatio" : {TYPE : 0x1E, SIZE : 2, MIN : 0, MAX : 9999,},
            "secondaryCurrentTransformerRatio" : {TYPE : 0x1F, SIZE : 1, MIN : 0, MAX : 5,},
            "primaryVoltageTransformerRatio" : {TYPE : 0x20, SIZE : 4, MIN : 30, MAX : 500000,},
            "secondaryVoltageTransformerRatio" : {TYPE : 0x21, SIZE : 2, MIN : 30, MAX : 500,},
        }
    };
    var encoded = [];
    var param = configObj.Param;
    var value = configObj.Value;
    if(param == null || value == null){
        return [];
    }

    var config = CONFIG_DEVICE.TYPES[param];
    if(config == null) {
        return [];
    }
    
    if(value < config.MIN || value > config.MAX) {
        return [];
    }

    if(config.CHANNEL != null)
    {
        encoded.push(config.CHANNEL);
    }else
    {
        encoded.push(CONFIG_DEVICE.CHANNEL);
    }
    encoded.push(config.TYPE);
    for(var i=1; i<=config.SIZE; i=i+1)
    {
        encoded.push( (value >> 8*(config.SIZE - i)) % 256 );
    }

    return encoded;
}

function encodePeriodicPackage(configObj)
{
    // Constants for device periodic package 
    var CONFIG_PERIODIC = {
        CHANNEL : 0xFF,
        TYPES : {
            "Interval" : {TYPE : 0x14, SIZE : 1, MIN : 1, MAX : 255,},
            "Mode" : {TYPE : 0x15, SIZE : 1, MIN : 0, MAX : 1,},
            "Status" : {TYPE : 0x16, SIZE : 1, MIN : 0, MAX : 1,},
            "Measurement" : {TYPE : 0x17, SIZE : 1, MIN : 0, MAX : 10,},
        },
        MEASUREMENTS : {
            index : 0x00,
            timestamp : 0x01,
            dataloggerTimestamp : 0x03,
            activeEnergyImportL123T1 : 0x04,
            activeEnergyImportL123T2 : 0x05,
            activeEnergyExportL123T1 : 0x06,
            activeEnergyExportL123T2 : 0x07,
            reactiveEnergyImportL123T1 : 0x08,
            reactiveEnergyImportL123T2 : 0x09,
            reactiveEnergyExportL123T1 : 0x0A,
            reactiveEnergyExportL123T2 : 0x0B,
            voltageL1N : 0x0C,
            voltageL2N : 0x0D,
            voltageL3N : 0x0E,
            currentL123 : 0x0F,
            currentL1 : 0x10,
            currentL2 : 0x11,
            currentL3 : 0x12,
            activePowerL123 : 0x13,
            activePowerL1 : 0x14,
            activePowerL2 : 0x15,
            activePowerL3 : 0x16,
            reactivePowerL1 : 0x17,
            reactivePowerL2 : 0x18,
            reactivePowerL3 : 0x19,
            apparentPowerL1 : 0x1A,
            apparentPowerL2 : 0x1B,
            apparentPowerL3 : 0x1C,
            powerFactorL1 : 0x1D,
            powerFactorL2 : 0x1E,
            powerFactorL3 : 0x1F,
            phaseAngleL1 : 0x20,
            phaseAngleL2 : 0x21,
            phaseAngleL3 : 0x22,
            frequency : 0x23,
            totalSystemActivePower : 0x24,
            totalSystemReactivePower : 0x25,
            totalSystemApparentPower : 0x26,
            maximumL1CurrentDemand : 0x27,
            maximumL2CurrentDemand : 0x28,
            maximumL3CurrentDemand : 0x29,
            averagePower : 0x2A,
            midYearOfCertification : 0x2B,
            manufacturedYear : 0xF0,
            firmwareVersion : 0xF1,
            hardwareVersion : 0xF2,
        }
    };

    var encoded = [];
    var config = {};
    var value = -1;
    if(configObj.Interval == null || configObj.Mode == null ||
        configObj.Status == null || configObj.Measurement == null){
        return [];
    }
    
    // Encode Interval, Mode, Status
    var fields = ["Interval", "Mode", "Status"];
    foreach(field: fields)
    {
        config = CONFIG_PERIODIC.TYPES[field];
        value = configObj[field];
        if(value < config.MIN || value > config.MAX){
            return [];
        }
        encoded.push(CONFIG_PERIODIC.CHANNEL);
        encoded.push(config.TYPE);
        encoded.push(value);
    }
    // Encode Measurement
    config = CONFIG_PERIODIC.TYPES.Measurement;
    if(configObj.Measurement.size < config.MIN || 
        configObj.Measurement.size > config.MAX){
        return [];
    }
    encoded.push(CONFIG_PERIODIC.CHANNEL);
    encoded.push(config.TYPE);
    foreach(field : configObj.Measurement)
    {
        if(CONFIG_PERIODIC.MEASUREMENTS[field] == null){
            return [];
        }
        encoded.push(CONFIG_PERIODIC.MEASUREMENTS[field]);
    }

    return encoded;
}
