// Process data from incoming message and metadata
var input = {
    fPort : parseInt(metadata.rc_fPort), // from rule chain
    confirmed : metadata.rc_confirmed === "true" ? true : false, // from rule chain
    data : msg,
    variables: null,
    devEui : metadata.cs_devEui, // from client scope
    devId  : metadata.cs_devId, // from client scope
    applicationId: metadata.cs_applicationId, // from client scope
};

// Constants for device configuration 
var CONFIG_DEVICE = {
    FPORT : 50,
    REGISTER_CHANNEL : 0xFF,
    PERIODIC_CHANNEL : 0xFF,
    PERIODIC_INTERVAL_TYPE : 0x14,
    PERIODIC_MODE_TYPE : 0x15,
    PERIODIC_STATUS_TYPE : 0x16,
    PERIODIC_REGISTERS_TYPE : 0x17,
    READING_TYPE : 0xCC,
    DATA_MAX_SIZE : 10,
    REGISTERS : {
        "rebootDevice": {TYPE: 0x0A, SIZE: 1, MIN: 1, MAX: 1, RIGHT:"WRITE_ONLY",},
        "restart": {TYPE: 0x0B, SIZE: 1, MIN: 1, MAX: 1, RIGHT:"WRITE_ONLY",},
        "adr": {TYPE: 0x12, SIZE: 1, MIN: 0, MAX: 1,},
        "sf": {TYPE: 0x13, SIZE: 1, MIN: 0, MAX: 6,},
        "lorawanWatchdogFunction": {TYPE: 0x14, SIZE: 1, MIN: 0, MAX: 1,},
        "lorawanWatchdogTimeout": {TYPE : 0x15, SIZE: 2, MIN: 1, MAX: 65535,},
        "lorawanWatchdogAlarm": {TYPE: 0x16, RIGHT:"READ_ONLY"},
        "defaultState": {TYPE: 0x64, SIZE: 1, MIN: 0, MAX: 2,},
        "timeoutState": {TYPE: 0x65, SIZE: 1, MIN: 0, MAX: 2,},
        "buttonOverrideFunction": {TYPE: 0x66, SIZE: 1, MIN: 0, MAX: 1,},
        "deviceOperationMode": {TYPE: 0x67, RIGHT:"READ_ONLY"},
        "buttonOverrideReset": {TYPE: 0x68, SIZE: 1, MIN: 1, MAX: 1, RIGHT:"WRITE_ONLY",},
        "internalCircuitTemperatureAlarm": {TYPE: 0x69, RIGHT:"READ_ONLY"},
        "internalCircuitTemperatureNumberOfAlarms": {TYPE: 0x70, RIGHT:"READ_ONLY"},
        "internalCircuitTemperature": {TYPE: 0x71, RIGHT:"READ_ONLY"},
        "internalCircuitHumidity": {TYPE: 0x72, RIGHT:"READ_ONLY"},
        "channel1State": {TYPE: 0x95, RIGHT:"READ_ONLY",},
        "channel1Control": {TYPE: 0x96, SIZE: 1, MIN: 0, MAX: 1,},
        "channel1Counter": {TYPE: 0x97, RIGHT:"READ_ONLY",},
        "channel2State": {TYPE: 0x98, RIGHT:"READ_ONLY",},
        "channel2Control": {TYPE: 0x99, SIZE: 1, MIN: 0, MAX: 1,},
        "channel2Counter": {TYPE: 0x9A, RIGHT:"READ_ONLY",},
    }
};

// Result object with encoded downlink payload
var result = {

    // downlink data content type: JSON, TEXT or BINARY (base64 format)
    contentType: "JSON",

    // downlink data
    data: JSON.stringify({
        downlinks:[{
            f_port: input.fPort,
            confirmed: input.confirmed,
            frm_payload: bytesToBase64(encodeDownlink(input).bytes),
            priority: "NORMAL"
        }]
    }),

    // Optional metadata object presented in key/value format
    metadata: {
        eui : input.devId,
        devId : input.devId
    }

};


return result;




