// Encode downlink function. (ChirpStack v4 , TTN)
//
// Input is an object with the following fields:
// - data = Object representing the payload that must be encoded.
// - variables = Object containing the configured device variables.
//
// Output must be an object with the following fields:
// - bytes = Byte array containing the downlink payload.
function encodeDownlink(input) {
    return {
        bytes: Encode(null, input.data, input.variables)
    };
}


/************************************************************************************************************/

// Encode encodes the given object into an array of bytes. (ChirpStack v3)
//  - fPort contains the LoRaWAN fPort number
//  - obj is an object, e.g. {"temperature": 22.5}
//  - variables contains the device variables e.g. {"calibration": "3.5"} (both the key / value are of type string)
// The function must return an array of bytes, e.g. [225, 230, 255, 0]
function Encode(fPort, obj, variables) {
    // Constants for downlink type
    var CONFIG_DOWNLINK = {
        TYPE    : "Type",
        CONFIG  : "Config",
        PERIODIC: "Periodic",
        READING : "Reading"
    };

    if(obj[CONFIG_DOWNLINK.TYPE] == CONFIG_DOWNLINK.CONFIG)
    {
        return encodeDeviceConfiguration(obj[CONFIG_DOWNLINK.CONFIG]);
    }
    else if(obj[CONFIG_DOWNLINK.TYPE] == CONFIG_DOWNLINK.PERIODIC)
    {
        return encodeUplinkConfiguration(obj[CONFIG_DOWNLINK.PERIODIC]);
    }
    else if(obj[CONFIG_DOWNLINK.TYPE] == CONFIG_DOWNLINK.READING)
    {
        return encodeParamtersReading(obj[CONFIG_DOWNLINK.READING]);
    }

    return [];
}

// Encode downlink function. (ChirpStack v4 , TTN)
//
// Input is an object with the following fields:
// - data = Object representing the payload that must be encoded.
// - variables = Object containing the configured device variables.
//
// Output must be an object with the following fields:
// - bytes = Byte array containing the downlink payload.
function encodeDownlink(input) {
    return {
        bytes: Encode(null, input.data, input.variables)
    };
}

/************************************************************************************************************/


function encodeDeviceConfiguration(configs)
{
    var encoded = [];

    foreach(configObj : configs)
    {
        var param = configObj.Param;
        var value = configObj.Value;
        if(value == null || param == null){
            return []; // error
        }

        var config = CONFIG_DEVICE.REGISTERS[param];
        if(config == null) {
            return []; // error
        }
        if(config.RIGHT != null && config.RIGHT == "READ_ONLY")
        {
            return []; // error
        } 
        if(value < config.MIN || value > config.MAX) {
            return []; // error
        }
        
        encoded.push(CONFIG_DEVICE.REGISTER_CHANNEL);
        encoded.push(config.TYPE);
        if(config.SIZE == 1)
        {
            encoded.push(value);
        }else if(config.SIZE == 2)
        {
            encoded.push( (value >> 8) % 256 );
            encoded.push( value % 256 );
        }else{
            return []; // error
        }
    }

    return encoded;
}

function encodeUplinkConfiguration(configObj)
{
    var encoded = [];
    var config = {};

    if(configObj.UplinkInterval == null || configObj.Mode == null ||
        configObj.Status == null || configObj.Registers == null){
        return []; // error
    }
    
    // Encode UplinkInterval, Mode, Status
    if(configObj.UplinkInterval < 0 || configObj.UplinkInterval > 255){
        return []; // error
    }
    encoded.push(CONFIG_DEVICE.PERIODIC_CHANNEL);
    encoded.push(CONFIG_DEVICE.PERIODIC_INTERVAL_TYPE);
    encoded.push(configObj.UplinkInterval);

    if(configObj.Mode < 0 || configObj.Mode > 1){
        return []; // error
    }
    encoded.push(CONFIG_DEVICE.PERIODIC_CHANNEL);
    encoded.push(CONFIG_DEVICE.PERIODIC_MODE_TYPE);
    encoded.push(configObj.Mode);
    
    if(configObj.Status < 0 || configObj.Status > 1){
        return []; // error
    }
    encoded.push(CONFIG_DEVICE.PERIODIC_CHANNEL);
    encoded.push(CONFIG_DEVICE.PERIODIC_STATUS_TYPE);
    encoded.push(configObj.Status);

    // Encode Registers
    if(configObj.Registers.size < 0 || 
        configObj.Registers.size > CONFIG_DEVICE.DATA_MAX_SIZE){
        return []; // error
    }
    encoded.push(CONFIG_DEVICE.PERIODIC_CHANNEL);
    encoded.push(CONFIG_DEVICE.PERIODIC_REGISTERS_TYPE);
    foreach(field : configObj.Registers)
    {
        config = CONFIG_DEVICE.REGISTERS[field];
        if(config == null){
            return []; // error
        }
        encoded.push(config.TYPE);
    }

    return encoded;
}

function encodeParamtersReading(configObj)
{
    var encoded = [];
    var config = {};

    if(configObj.length > CONFIG_DEVICE.DATA_MAX_SIZE)
    {
        return []; // error
    }
    encoded.push(CONFIG_DEVICE.REGISTER_CHANNEL);
    encoded.push(CONFIG_DEVICE.READING_TYPE);
    foreach(param : configObj)
    {
        config = CONFIG_DEVICE.REGISTERS[param];
        if(config == null)
        {
            return []; // error
        }
        if(config.RIGHT != null && config.RIGHT == "WRITE_ONLY")
        {
            return [];  // error
        }
        encoded.push(config.TYPE);
    }

    return encoded;
}
