/**
 * Decodes the incoming payload and returns a structured object containing telemetry data and attributes.
 *
 * @param {number[]} input - The raw payload received as an array of bytes.
 * @returns {Object} output - The structured output with decoded telemetry and attributes.
 */

function decodePayload(input) {
    // Initialize the output object with empty attributes and telemetry for clarity.
    var result = { attributes: {}, telemetry: {}};

    // Decode serial number (SN) from the first 4 bytes of the payload.
    // Press '?' icon in the top right corner to learn more about built in helper functions and capabilities.
    result.attributes.sn = parseBytesToInt(input, 0, 4);

    // Extract the timestamp from metadata (represented in milliseconds).
    var timestamp = metadata.ts; // ts from the incoming message.

    // Initialize an object to store decoded key/value telemetry data.
    var values = {};

    // Decode battery level from the 5th byte of the payload.
    values.battery = parseBytesToInt(input, 4, 1);

    // Decode temperature from the 6th and 7th bytes of the payload (divided by 100).
    values.temperature = parseBytesToInt(input, 5, 2) / 100.0;

    // Decode saturation from the 8th byte of the payload.
    values.saturation = parseBytesToInt(input, 7, 1);

    // Combine the timestamp with values and add it to the telemetry.
    result.telemetry = {
        ts: timestamp,
        values: values
    };

    // Return the fully constructed output object.
    return result;
    // Same logic, less code:
    // return {
    //     attributes: {
    //         sn: parseBytesToInt(input, 0, 4)
    //     },
    //     telemetry: {
    //         ts: metadata.ts,
    //         values: {
    //             battery: parseBytesToInt(input, 4, 1),
    //             temperature: parseBytesToInt(input, 5, 2) / 100.0,
    //             saturation: parseBytesToInt(input, 7, 1)
    //         }
    //     }
    // };
}

var result = decodePayload(payload);
// Uncomment this code block to overwrite values set in the main configuration window. Useful if you extract device/asset/customer/group names from the payload;
// result.type = 'DEVICE'; // Entity type allows you to choose type of created entity. Can be 'DEVICE' or 'ASSET'.
// result.name = 'Temperature Sensor'; // Device or asset name (the value must be unique)
// result.profile = 'IndustrialSensorProfile'; // Device or asset profile name.
// result.customer = 'MyCustomer'; // If customer is not null - created entity will be assigned to customer with such name.
// result.group = 'SensorsGroup'; // If group is not null - created entity will be added to the entity group with such name.

// Return the final result object.
return result;

/**
 * Parse a slice of bytes from an array into an integer (big-endian).
 *
 * @param {number[]} input - The array of bytes.
 * @param {number} offset - The starting index.
 * @param {number} length - The number of bytes to convert.
 * @returns {number} - The resulting integer.
 */
function parseBytesToInt(input, offset, length) {
  var result = 0;
  for (var i = offset; i < offset + length; i++) {
    result = (result << 8) | (input[i] & 0xFF);
  }
  return result;
}

configuration.tbelDecoder:
/**
 * Decodes the incoming payload and returns a structured object containing telemetry data and attributes.
 *
 * @param {byte[]} input - The raw payload received as an array of bytes.
 * @returns {Object} output - The structured output with decoded telemetry and attributes.
 */

function decodePayload(input) {
    // Initialize the output object with empty attributes and telemetry for clarity.
    var result = { attributes: {}, telemetry: {}};


    // Extract the timestamp from metadata (represented in milliseconds).
    var timestamp = metadata.ts; // ts is the timestamp parsed from the incoming message's time, or returns the current time if it cannot be parsed.

    // Initialize an object to store decoded key/value telemetry data.
    var values = {};
    var index = 0;
    var headerByte = input[index++];
    result.attributes.uplinkReasonButton = ((headerByte & 1) != 0);

    if (result.attributes.uplinkReasonButton) {
        result.attributes.buttonClickReason = 'single';
    }

    result.attributes.uplinkReasonMovement = ((headerByte & 2) != 0);
    result.attributes.uplinkReasonGpio = ((headerByte & 4) != 0);
    result.attributes.containsGps = ((headerByte & 8) != 0);
    result.attributes.containsOnboardSensors = ((headerByte & 16) != 0);
    result.attributes.containsSpecial = ((headerByte & 32) != 0);
    result.attributes.crc = Integer.toHexString(input[index++] & 0xFF);
    values.batteryLevel = parseBytesIntToFloat(input, index, 1);
    index += 1;

    if (result.attributes.containsOnboardSensors != null && result.attributes.containsOnboardSensors) {
        var sensorContent = input[index++];
        result.attributes.containsTemperature = ((sensorContent & 1) != 0);
        result.attributes.containsLight = ((sensorContent & 2) != 0);
        result.attributes.containsAccelerometerCurrent = ((sensorContent & 4) != 0);
        result.attributes.containsAccelerometerMax = ((sensorContent & 8) != 0);
        result.attributes.containsWifiPositioningData = ((sensorContent & 16) != 0);
        result.attributes.buttonEventInfo = ((sensorContent & 32) != 0);
        result.attributes.containsExternalSensors = ((sensorContent & 64) != 0);
        result.attributes.containsBluetoothData = false;

        var buttonHeader = result.attributes.uplinkReasonButton;
        var buttonEvent = result.attributes.buttonEventInfo;
        if (!buttonEvent && !buttonHeader) {
          result.attributes.buttonClickReason = 'none';
        } else if (!buttonEvent && buttonHeader) {
          result.attributes.buttonClickReason = 'single';
        } else if (buttonEvent && !buttonHeader) {
          result.attributes.buttonClickReason = 'long';
          result.attributes.uplinkReasonButton = true;
        } else if (buttonEvent && buttonHeader) {
          result.attributes.buttonClickReason = 'double';
        }

        var hasSecondSensorContent = (sensorContent & 128) != 0;

        if (hasSecondSensorContent) {
          var sensorContent2 = input[index++];
          result.attributes.containsBluetoothData = (sensorContent2 & 1) != 0;
          result.attributes.containsRelativeHumidity = (sensorContent2 & 2) != 0;
          result.attributes.containsAirPressure = (sensorContent2 & 4) != 0;
          result.attributes.containsManDown = (sensorContent2 & 8) != 0;
          result.attributes.containsTilt = (sensorContent2 & 16) != 0;
          result.attributes.containsRetransmitCnt = (sensorContent2 & 32) != 0;
        }

        if (result.attributes.containsTemperature) {
          values.temperature = parseBytesToInt(input, index, 2) / 100;
          index += 2;
        }

        if (result.attributes.containsLight != null && result.attributes.containsLight) {
          var value = parseBytesToInt(input, index, 2);
          var exponent = value >> 12 & 0xFF;
          values.lightIntensity = Float.valueOf((value & 0x0FFF) << exponent) / 100;
          index += 2;
        }

        if (result.attributes.containsAccelerometerCurrent != null && result.attributes.containsAccelerometerCurrent) {
          values.accelerometer = {
            x: parseBytesIntToFloat(input, index, 2) / 1000,
            y: parseBytesIntToFloat(input, index + 2, 2) / 1000,
            z: parseBytesIntToFloat(input, index + 4, 2) / 1000
          };

          index += 6;
        }

        if (result.attributes.containsAccelerometerMax != null && result.attributes.containsAccelerometerMax) {
          values.maxAccelerationNew = parseBytesIntToFloat(input, index, 2) / 1000;
          values.maxAccelerationHistory = parseBytesIntToFloat(input, index + 2, 2) / 1000;

          index += 4;
        }

        if(result.attributes.containsWifiPositioningData != null && result.attributes.containsWifiPositioningData) {
            var wifiInfo = input[index++];
            var numAccessPoints = wifiInfo & 7;

            var wifiStatus = ((wifiInfo & 8) >> 2) + ((wifiInfo & 16) >> 3);
             var containsSignalStrength = wifiInfo & 32;
             var wifiStatusDescription = "";

            switch (wifiStatus) {
                case 0:
                  wifiStatusDescription = 'success';
                  break;
                case 1:
                  wifiStatusDescription = 'failed';
                  break;
                case 2:
                  wifiStatusDescription = 'no_access_points';
                  break;
                default:
                  wifiStatusDescription = "unknown (" + wifiStatus + ")";
            }

            values.wifiInfo = {
                status: wifiStatusDescription,
                statusCode: wifiStatus,
                accessPoints: []
            };

            for (int i = 0; i < numAccessPoints; i++) {
                var macParts = [];
                for (int j = 0; j < 6; j++) {
                    macParts[j] = String.format("%02x", input[index++] & 0xFF);
                }
                var macAddress = String.join(":", macParts);

                var signalStrength = null;
                if (containsSignalStrength != 0) {
                    signalStrength = input[index++];
                }

                values.wifiInfo.accessPoints.push({
                    macAddress: macAddress,
                    signalStrength: signalStrength
                });
            }
        }

        if (result.attributes.containsExternalSensors != null && result.attributes.containsExternalSensors) {
            var type = input[index++];

            if (type === 0x0A) {
              values.externalSensor = {
                type: 'battery',
                batteryA: parseBytesToInt(input, index, 2),
                batteryB: parseBytesToInt(input, index + 2, 2)
              };
              i += 4;
            } else if (type === 0x64) {
              values.externalSensor = {
                type: 'externalTemperature',
                value: parseBytesToInt(input, index, 2) / 100
              };
              i += 2;
            } else if (type === 0x65) {
              values.externalSensor = {
                type: 'detectSwitch',
                value: input[index++]
              };
            } else if (type === 0x66) {
                var iobuttonStateData = input[index++];
                var iobuttonState = (iobuttonStateData & 0xF0) >> 4;
                var iobuttonStateClickCnt = iobuttonStateData & 0x0F;

                if (iobuttonState === 0) {
                    iobuttonState = 'Idle';
                } else if (iobuttonState === 1) {
                    iobuttonState = 'Calling';
                } else if (iobuttonState === 2) {
                    iobuttonState = 'Success';
                } else if (iobuttonState === 3) {
                    iobuttonState = 'Cleared';
                } else {
                    iobuttonState = 'Undefined';
                }

                values.externalSensor = {
                    type: 'buttonState',
                    state: iobuttonState,
                    clickCnt: iobuttonStateClickCnt
                };
            }
        }

        if(result.attributes.containsBluetoothData != null && result.attributes.containsBluetoothData) {
            var bluetoothInfo = input[index++];
            var numBeacons = bluetoothInfo & 7;
            var bluetoothStatus = bluetoothInfo >> 3 & 0x03;
            var addSlotInfo = bluetoothInfo >> 5 & 0x03;
            var bluetoothStatusDescription = "";

            if (bluetoothStatus == 0) {
                bluetoothStatusDescription = "success";
            }
            else if (bluetoothStatus == 1) {
                bluetoothStatusDescription = "failed";
            }
            else if (bluetoothStatus == 2) {
                bluetoothStatusDescription = "no_access_points";
            }
            else {
                bluetoothStatusDescription = "unknown (" + bluetoothStatus + ")";
            }

            values.bluetoothInfo = {
                status: bluetoothStatusDescription,
                statusCode: bluetoothStatus,
                addSlotInfo: addSlotInfo,
                beacons: []
            };

            for (var i = 0; i < numBeacons; i++) {
                var beaconBluetoothBeaconsResult = null;
                if (addSlotInfo == 0x00) {
                    beaconBluetoothBeaconsResult = parseBluetoothBeacons00(input, index);
                }
                else if (addSlotInfo == 0x01) {
                    beaconBluetoothBeaconsResult = parseBluetoothBeacons01(input, index);
                }
                else if (addSlotInfo == 0x02) {
                    beaconBluetoothBeaconsResult = parseBluetoothBeacons02(input, index);
                }

                if (beaconBluetoothBeaconsResult != null) {
                    values.bluetoothInfo.beacons.push(beaconBluetoothBeaconsResult.beacon);
                    index += beaconBluetoothBeaconsResult.index;
                }
            }
        }

        if (result.attributes.containsRelativeHumidity != null && result.attributes.containsRelativeHumidity) {
           values.humidity = parseBytesToInt(input, index, 2) / 100;
           i += 2;
        }

        if (result.attributes.containsAirPressure != null && result.attributes.containsAirPressure) {
           values.pressure = parseBytesToInt(input, index, 3);
           i += 3;
        }

        if (result.attributes.containsManDown != null && result.attributes.containsManDown) {
           var manDownData = (input[index++]);
           var manDownState = (manDownData & 0x0f);
           var manDownStateLabel = "";

           if (manDownState === 0x00) {
               manDownStateLabel = 'ok';
           } else if (manDownState === 0x01) {
               manDownStateLabel = 'sleeping';
           } else if (manDownState === 0x02) {
               manDownStateLabel = 'preAlarm';
           } else if (manDownState === 0x03) {
               manDownStateLabel = 'alarm';
           } else {
               manDownStateLabel = manDownState + '';
           }

           values.manDown = {
               state: manDownStateLabel,
               positionAlarm: (manDownData & 0x10) != 0,
               movementAlarm: (manDownData & 0x20) != 0
           };
        }

        if (result.attributes.containsTilt != null && result.attributes.containsTilt) {
            values.tilt = {
              currentTilt: parseBytesToInt(input, index, 2) / 100,
              currentDirection: Math.round(input[index + 2] * (360/255)),
              maximumTiltHistory: parseBytesToInt(input, index + 3, 2) / 100,
              DirectionHistory: Math.round(input[index + 5] * (360/255)),
            };
            index += 6;
        }

        if (result.attributes.containsRetransmitCnt != null && result.attributes.containsRetransmitCnt) {
            values.retransmitCnt = input[index++];
            index += 1;
        }
    }

    if (result.attributes.containsGps != null && result.attributes.containsGps) {
        values.gps = {};
        values.gps.navStat = input[index++];
        values.gps.latitude = parseBytesIntToFloat(input, index, 4) / 10000000;
        values.gps.longitude = parseBytesIntToFloat(input, index + 4, 4) / 10000000;
        values.gps.altRef = parseBytesIntToFloat(input, index + 8, 2) / 10;
        values.gps.hAcc = input[index + 10];
        values.gps.vAcc = input[index + 11];
        values.gps.sog = parseBytesIntToFloat(input, index + 12, 2) / 10;
        values.gps.cog = parseBytesIntToFloat(input, index + 14, 2) / 10;
        values.gps.hdop = input[index + 16] / 10;
        values.gps.numSvs = input[index + 17];
        index += 18;
    }

    // Combine the timestamp with values and add it to the telemetry.
    result.telemetry = {
        ts: timestamp,
        values: values
    };

    // Return the fully constructed output object.
    return result;
}

var uplinkDataResult = [];

var result = decodePayload(payload);

uplinkDataResult.push(result);

// Return the final result object.
return uplinkDataResult;

function substringToHex(source, int offset, int length) {
    var hex = "";
    for (int i = 0; i < length; i++) {
        hex += String.format("%02x", source[offset + i] & 0xFF);
    }
    return hex;
}

function parseBluetoothBeacons00(bytes, i) {
  var beaconStatus = bytes[i++];
  var beaconType = beaconStatus & 0x03;
  var rssiRaw = beaconStatus >> 2;
  var rssi = 27 - rssiRaw * 2;
  var beacon = "";

  if (beaconType === 0x00) {
        var bluetoothResult = {
          beacon : {
              type: 'ibeacon',
              rssi: rssi,
              uuid: substringToHex(bytes, i, 2),
              major: substringToHex(bytes, i + 2, 2),
              minor: substringToHex(bytes, i + 4, 2)
            },
          index: 6
        };
        return bluetoothResult;
  } else if (beaconType === 0x01) {
      var bluetoothResult = {
          beacon : {
              type: 'eddystone',
              rssi: rssi,
              instance: substringToHex(bytes, i, 6)
            },
          index: 6
       };
    return bluetoothResult;
  } else if (beaconType === 0x02) {
      var bluetoothResult = {
          beacon : {
              type: 'altbeacon',
              rssi: rssi,
              id1: substringToHex(bytes, i, 2),
              id2: substringToHex(bytes, i + 2, 2),
              id3: substringToHex(bytes, i + 4, 2)
            },
          index: 6
       };
    return bluetoothResult;
  } else if (beaconType === 0x03) {
      var bluetoothResult = {
          beacon : {
              type: 'fullbeacon',
              rssi: rssi,
              id1: substringToHex(bytes, i, 2),
              id2: substringToHex(bytes, i + 2, 2),
              id3: substringToHex(bytes, i + 4, 2)
            },
          index: 6
       };
     return bluetoothResult;
  } else {
    return null;
  }
}

function parseBluetoothBeacons01(bytes, i) {
    var beaconStatus = bytes[i++];
    var beaconType = beaconStatus & 0x03;
    var rssiRaw = beaconStatus >> 2;
    var rssi = 27 - rssiRaw * 2;

    if (beaconType === 0x00) {
        var bluetoothResult = {
          beacon : {
              type: 'ibeacon',
              rssi: rssi,
              uuid: substringToHex(bytes, i, 16),
              major: substringToHex(bytes, i + 16, 2),
              minor: substringToHex(bytes, i + 16, 2)
            },
          index: 20
       };
     return bluetoothResult;
    } else if (beaconType === 0x01) {
        var bluetoothResult = {
            beacon : {
                type: 'eddystone',
                rssi: rssi,
                namespace: substringToHex(bytes, i, 10),
                instance: substringToHex(bytes, i + 10, 6)
            },
            index: 16
        };
        return bluetoothResult;
    } else if (beaconType === 0x02) {
        var bluetoothResult = {
            beacon : {
                type: 'altbeacon',
                rssi: rssi,
                id1: substringToHex(bytes, i, 16),
                id2: substringToHex(bytes, i + 16, 2),
                id3: substringToHex(bytes, i + 18, 2)
            },
            index: 20
        };
        return bluetoothResult;
    } else if (beaconType === 0x03) {
        var bluetoothResult = {
            beacon : {
                type: 'fullbeacon',
                rssi: rssi,
                id1: substringToHex(bytes, i, 16),
                id2: substringToHex(bytes, i + 16, 2),
                id3: substringToHex(bytes, i + 18, 2)
            },
            index: 20
        };
        return bluetoothResult;
    } else {
        return null;
    }
}

function parseBluetoothBeacons02(bytes, i) {
    var beaconStatus = bytes[i++];
    var beaconType = beaconStatus & 0x03;
    var slotMatch = beaconStatus >> 2 & 0x07;
    var rssiRaw = beaconStatus >> 2;
    var rssi = 27 - rssiRaw * 2;

    if (beaconType === 0x00) {
        var bluetoothResult = {
            beacon : {
                type: 'ibeacon',
                rssi: rssi,
                slot: slotMatch,
                major: substringToHex(bytes, i, 2),
                minor: substringToHex(bytes, i + 2, 2)
            },
            index: 4
        };
        return bluetoothResult;
    } else if (beaconType === 0x01) {
        var bluetoothResult = {
            beacon : {
                type: 'eddystone',
                rssi: rssi,
                slot: slotMatch,
                instance: substringToHex(bytes, i, 6)
            },
            index: 6
        };
        return bluetoothResult;
    } else if (beaconType === 0x02) {
        var bluetoothResult = {
            beacon : {
                type: 'altbeacon',
                rssi: rssi,
                slot: slotMatch,
                id2: substringToHex(bytes, i, 2),
                id3: substringToHex(bytes, i + 2, 2)
            },
            index: 4
        };
        return bluetoothResult;
    } else if (beaconType === 0x03) {
        var bluetoothResult = {
            beacon : {
                type: 'fullbeacon',
                rssi: rssi,
                slot: slotMatch,
                id2: substringToHex(bytes, i, 2),
                id3: substringToHex(bytes, i + 2, 2)
            },
            index: 4
        };
        return bluetoothResult;
    } else {
        return null;
    }
}
